INSERT INTO `punch` VALUES ('aa09f362-cb30-40b4-9340-e918ee4c2252', 'oVE3y0KxkwqKIJpGwCCOV-ZDxElA', '1531217902060', '1531217902060', '1', '1', '0');
INSERT INTO `xwDetail` VALUES ('1', '三焦俞', '7', 'Sānjiāoshū', 'sjs', 'BL22', '三焦之背俞穴', '', '在腰部，当第1腰椎棘突下，旁开1.5寸。', '第1步：正坐或俯卧位；<br>第2步：两侧骨盆最高点（髂嵴最高点)连线与后正中线的交点处，为第4腰椎棘突；<br>第3步：从第4腰椎棘突垂直往上推3个椎体，即是第1腰椎棘突；<br>第4步：在第1腰椎棘突下有一凹陷，此凹陷旁开2横指(食指、中指并拢，以中指近端指间关节横纹水平的二指宽度为1.5寸），即为本穴。', '①肠鸣、腹胀、腹泻、水肿等脾胃疾患；②腰背强痛；③腹水，神经衰弱，腰肌劳损。', '隔物灸仪艾灸时间：30-50分钟；温度：38-50℃；<br>艾条悬灸时间：10-15分钟；<br>艾炷灸时间：5-7壮。', '①现代常用于治疗肾炎、尿潴留、胃炎、胃痉挛等。配小肠俞、下髎、章门主治肠鸣腹泻；配肾俞、委中、太溪、命门主治腰脊强痛。<br>②《马氏温灸法》：可治消化不良所致的慢性荨麻疹。', '086三焦俞肾俞_opt', '1', '①肠鸣、腹胀、腹泻、水肿等脾胃疾患；②腰背强痛；③腹水，神经衰弱，腰肌劳损。', '肠鸣,腹胀,腹泻,神经衰弱,腰肌劳损', '①现代常用于治疗肾炎、尿潴留、胃炎、胃痉挛等。配小肠俞、下髎、章门主治肠鸣腹泻；配肾俞、委中、太溪、命门主治腰脊强痛。\r\n②《马氏温灸法》：可治消化不良所致的慢性荨麻疹。', '肾炎,尿潴留（癃闭）,慢性胃炎,胃痉挛,荨麻疹', '隔物灸仪艾灸时间：30-50分钟；温度：38-50℃；\r\n艾条悬灸时间：10-15分钟；\r\n艾炷灸时间：5-7壮。', '自我按摩，用拇指的指腹端揉按此穴。', '', '', '直刺0．5～1寸。', '', '');
INSERT INTO `xwDetail` VALUES ('2', '三角灸', '15', 'Sānjiǎojiū', 'sjj', '', '', '', '以患者两口角之间的长度为一边，作等边三角形，将顶角置于脐中心，底边成水平线，两底角处取穴。', '第1步：仰卧位；<br>第2步：以被取穴者两口角的长度为边长，作一等边三角形；<br>第3步：将顶点置于肚脐中点，底边呈水平线，则两底角处，按压有酸胀感，即为本穴。', '①疝气奔豚；②绕脐疼痛；③不孕。', '隔物灸仪艾灸时间：30-70 分钟；温度：38-52 ℃；<br>艾条悬灸时间：10-20分钟；<br>艾炷灸时间：5-7壮。', '', '三角灸利尿子宫_opt', '2', '①疝气奔豚；②绕脐疼痛；③不孕。', '不孕症', '', '', '隔物灸仪艾灸时间：30-70 分钟；温度：38-52 ℃；\r\n艾条悬灸时间：10-20分钟；\r\n艾炷灸时间：5-7壮。', '', '', '', '', '', '');
INSERT INTO `xwDetail` VALUES ('3', '上脘', '14', 'Shàngwǎn', 'sw', 'RN13', '任脉、手太阳、足阳明经交会穴', '', '在上腹部，前正中线上，当脐中上5寸。', '仰卧位。取一标有二等分的弹性皮筋，将皮筋的两头与肚脐、胸剑联合部对齐拉紧，从皮筋的中点直上量1横指处，即为本穴。', '①胃痛、呕吐、腹胀、纳呆、腹痛、呕逆等脾胃病证；②癫痫。', '隔物灸仪艾灸时间： 30-50分钟；温度：38-50 ℃；<br>艾条悬灸时间：10-20分钟；<br>艾炷灸时间：5-7壮。', '现代常用于治疗胃炎、胃痉挛、胃溃疡、胃下垂等。配中脘主治胃脘疼痛、饮食不化；配神门主治失眠、狂躁。', '上脘巨阙鸠尾中庭_opt', '11', '①胃痛、呕吐、腹胀、纳呆、腹痛、呕逆等脾胃病证；②癫痫。', '呕吐,腹胀,腹痛,癫痫', '现代常用于治疗胃炎、胃痉挛、胃溃疡、胃下垂等。配中脘主治胃脘疼痛、饮食不化；配神门主治失眠、狂躁。', '慢性胃炎,胃痉挛,胃下垂,失眠', '隔物灸仪艾灸时间： 30-50分钟；温度：38-50 ℃；\r\n艾条悬灸时间：10-20分钟；\r\n艾炷灸时间：5-7壮。', '用手食指中指的指腹揉按此穴。', '', '', '直刺0.5-1寸。本穴位在腹白线上，深部位肝下缘及幽门部位，肝脾肿大的病人，其边缘在本穴处或以下者，禁不可刺。本穴不用提插和粗针疗法，以免造成胃穿孔和化脓性腹膜炎。《玉龙歌》“九中心痛及脾痛，上脘穴内用神针，若还脾败中脘补，两针神效免穴侵”\r\n上脘、中脘、下脘同治胃肠病，但三穴各有特长。下脘穴，散而去之，治胃兼通肠腹。中脘，和而消之，治胃兼生中气。上脘，抑而降之，治胃兼宽胸膈。', '', '');
INSERT INTO `xwDetail` VALUES ('4', '上迎香', '15', 'Shàngyíngxiāng', 'syx', 'EX-HN8', '', '', '仰靠坐位。在面部，当鼻翼软骨与鼻甲交界的粘膜处。', '正坐或仰卧位。沿鼻侧鼻唇沟向上推，至上端尽处有一凹陷处，即为本穴。', '①鼻塞，鼻渊，鼻息肉等鼻疾；②暴发火眼，迎风流泪。', '艾条悬灸：7-10分钟。', '现代常用于治疗鼻炎、鼻窦炎、结膜炎、泪囊炎等。配印堂、合谷、肺俞主治鼻塞、鼻渊。', '印堂鱼腰球后上迎香_opt', '12', '①鼻塞，鼻渊，鼻息肉等鼻疾；②暴发火眼，迎风流泪。', '鼻渊,迎风流泪', '现代常用于治疗鼻炎、鼻窦炎、结膜炎、泪囊炎等。配印堂、合谷、肺俞主治鼻塞、鼻渊。', '过敏性鼻炎,急性结膜炎,鼻塞', '艾条悬灸：7-10分钟。', '双手食指重叠用指腹揉按此穴。', '', '', '轻压眼球向上，向眶缘缓慢直刺0.5-1.5寸，不提插。', '', '');
INSERT INTO `xwDetail` VALUES ('5', '三间', '2', 'Sānjiān', 'sj', 'LI3', '输穴', '', '微握拳，在食指桡侧第2掌指关节后凹陷中。', '伸臂，微握拳。沿食指桡侧的掌背交界线（赤白肉际）轻推，至食指第2掌指关节后缘触及一凹陷处，即为本穴。', '①齿痛、咽喉肿痛；②腹胀，肠鸣；③嗜睡；④手指肿痛，肩周炎。', '艾条悬灸：5-10分钟；米粒灸：3-5壮。', '现代常用于治疗扁桃体炎、咽喉炎、痢疾肠炎等。', '商阳二间三间_opt', '3', '①齿痛、咽喉肿痛；②腹胀，肠鸣；③嗜睡；④手指肿痛，肩周炎。', '牙痛,腹胀,肠鸣,嗜睡,肩周炎', '现代常用于治疗扁桃体炎、咽喉炎、痢疾肠炎等。', '扁桃体炎,咽喉炎,痢疾', '艾条悬灸：5-10分钟；米粒灸：3-5壮。', '自我按摩，用食指远端指腹点按二间穴5-10次。', '', '', '直刺0.3-0.5寸。局部麻胀，向手背放散。《金针梅花诗钞》“急食不通二间效，五指拘挛三间长”，五指拘挛多见于中风后遗症，指屈肌张力过高所致，可取三间为好（必须深刺，透向后溪穴或合谷穴）。《甲乙经》卷八“寒热，唇口干，喘急，目内眦急痛，善惊，三间主之”可以从张颖清教授生物全息律第二掌侧骨考虑，更容易理解。亦可以从董氏奇穴灵骨大白穴的倒马刺考虑。', '', '');
INSERT INTO `xwDetail` VALUES ('6', '三阳络', '10', 'Sānyángluò', 'syl', 'SJ8', '', '', '在前臂背侧，腕背横纹上4寸，尺骨与桡骨之间。', '抬臂俯掌。从掌腕背横纹中点处直上量4横指即支沟（参见“支沟”），再从支沟直上量1横指，在前臂两骨头之间可触及一凹陷，按压有酸胀感，即为本穴。', '①上肢痹痛；②暴喑，耳聋，齿痛等五官疾患；③感冒；④脑血管后遗症，失语。', '隔物灸仪艾灸时间：20-30分钟；温度：38-48℃；<br>艾条悬灸时间：10-20分钟；<br>艾炷灸时间：3-5壮。', '现代常用于治疗前臂疼痛麻木、神经性耳聋等。配曲池、臂臑主治手臂疼痛；配听宫、中渚主治耳聋。', '127三阳络四渎_opt', '4', '①上肢痹痛；②暴喑，耳聋，齿痛等五官疾患；③感冒；④脑血管后遗症，失语。', '耳鸣、耳聋,牙痛,感冒,失语', '现代常用于治疗前臂疼痛麻木、神经性耳聋等。配曲池、臂臑主治手臂疼痛；配听宫、中渚主治耳聋。', '', '隔物灸仪艾灸时间：20-30分钟；温度：38-48℃；\r\n艾条悬灸时间：10-20分钟；\r\n艾炷灸时间：3-5壮。', '自我按摩，用手拇指或食指的指腹端轻轻揉按此穴。', '', '', '直刺0.5～1寸。斜刺透郄门深2-3寸，不断捻转、运针，针感上传肘部，可治疗胸部疼痛及用于肺切除手术时的疼痛。', '', '');
INSERT INTO `xwDetail` VALUES ('7', '上关', '11', 'Shàngguān', 'sg', 'GB3', '手足少阳，足阳明经交会穴', '', '在耳前，下关直上，当颧弓的上缘凹陷处。', '正坐或侧卧位。从耳屏往前量2横指（食、中指），耳前颧骨弓上侧可触及一凹陷，按压有酸痛感，即为本穴。', '①耳鸣、耳聋、聤耳等耳疾；②齿痛、口眼歪斜、面痛、偏头痛、口噤等头面疾患；③惊痫，瘈疭。', '隔物灸仪艾灸时间：20-40分钟；温度：38-45℃；<br>艾条悬灸时间：10-20分钟；<br>艾炷灸时间3-5壮。', '现代常用于治疗神经性耳鸣（耳聋）、中耳炎、面神经麻痹、三叉神经痛等。配太阳、外关主治偏头痛。', '瞳子髎听会上关_opt', '6', '①耳鸣、耳聋、聤耳等耳疾；②齿痛、口眼歪斜、面痛、偏头痛、口噤等头面疾患；③惊痫，瘈疭。', '耳鸣、耳聋,聤耳,牙痛,偏头痛,口噤', '现代常用于治疗神经性耳鸣（耳聋）、中耳炎、面神经麻痹、三叉神经痛等。配太阳、外关主治偏头痛。', '化脓性中耳炎,三叉神经痛', '隔物灸仪艾灸时间：20-40分钟；温度：38-45℃；\r\n艾条悬灸时间：10-20分钟；\r\n艾炷灸时间3-5壮。', '自我按摩，用手拇指或食指的指腹端轻轻揉按此穴。', '', '', '直刺0.3～0.5寸。《甲乙》卷十一“刺太深，令人耳无闻”。《素问。刺禁论篇》“刺客主人，内陷中脉，为内漏为聋”。', '', '');
INSERT INTO `xwDetail` VALUES ('8', '上巨虚', '3', 'Shàngjùxū', 'sjx', 'ST37', '大肠下合穴', '', '在小腿前外侧，当犊鼻下6寸，距胫骨前缘1横指（中指)。', '第1步：坐位屈膝；<br>第2步：在膝盖外侧可触及一凹陷，即为犊鼻穴；<br>第3步：从犊鼻穴往下量两个4横指（食指、中指、无名指、小指四指并拢，以中指近端指间关节横纹水平的四指宽度为3寸，也称一夫法），距胫骨外侧前缘1横指（中指）处，即为本穴。', '①肠鸣、腹痛、腹泻、便秘，肠痈等肠胃疾患；②下肢痿痹。', '隔物灸仪艾灸时间：30-60分钟；温度：38-50℃；<br>艾条悬灸时间：10-20分钟；<br>艾炷灸时间：5-9壮。', '现代常用于治疗急性细菌性痢疾、急性肠炎、单纯性阑尾炎等。配天枢、曲池治疗细菌性痢疾；配支沟、大肠俞主治便秘。<br>甄权云：治脏气不足，偏风腰腿手足不仁，灸随年为壮。', '上巨虚_opt', '7', '①肠鸣、腹痛、腹泻、便秘，肠痈等肠胃疾患；②下肢痿痹。', '肠鸣,腹痛,腹泻,便秘', '现代常用于治疗急性细菌性痢疾、急性肠炎、单纯性阑尾炎等。配天枢、曲池治疗细菌性痢疾；配支沟、大肠俞主治便秘。\r\n甄权云：治脏气不足，偏风腰腿手足不仁，灸随年为壮。', '痢疾,阑尾炎,便秘', '隔物灸仪艾灸时间：30-60分钟；温度：38-50℃；\r\n艾条悬灸时间：10-20分钟；\r\n艾炷灸时间：5-9壮。', '自我按摩，用拇指的指腹端轻轻揉按此穴。或握拳用指间关节叩击此处。', '', '', ' 直刺1-2寸。局部酸胀。针尖略向上斜刺，其针感沿本经循膝股走至腹部，少数病例可走至上腹部和胸部。略向下斜刺，其针感沿足阳明胃经走至足跗、足趾部。《灵枢.邪气脏腑病形》篇“大肠病者，肠中切痛，而鸣濯濯，冬日重感与寒即泄，当脐而痛，不能久立，与胃同候，取巨虚上廉”。', '', '');
INSERT INTO `xwDetail` VALUES ('9', '上廉', '2', 'Shànglián', 'sl', 'LI9', '', '', '在前臂背面桡侧，当阳溪穴与曲池穴连线上，肘横纹下3寸处。', '伸臂俯掌。先确定阳溪（参见“阳溪”）与曲池（参见“曲池”）的位置，从肘横纹沿阳溪与曲池的连线向下量4横指处，即为本穴。', '①肘臂痛，半身不遂，手臂麻木；②头痛；③肠鸣腹痛；④脑血管病后遗症。', '隔物灸仪艾灸时间：38-40分钟；温度：38-45℃；<br>艾条悬灸时间：5-10分钟；<br>艾炷灸时间：3-5壮。', '现代常用于治疗臂神经痛、上肢麻木、瘫痪、肠炎等。配肩髃、合谷主治上肢麻木、疼痛。', '012上廉手三里曲池_opt', '8', '①肘臂痛，半身不遂，手臂麻木；②头痛；③肠鸣腹痛；④脑血管病后遗症。', '头痛,肠鸣,腹痛', '现代常用于治疗臂神经痛、上肢麻木、瘫痪、肠炎等。配肩髃、合谷主治上肢麻木、疼痛。', '', '隔物灸仪艾灸时间：38-40分钟；温度：38-45℃；\r\n艾条悬灸时间：5-10分钟；\r\n艾炷灸时间：3-5壮。', '自我按摩，用拇指远端指腹点按弹拨该穴。', '', '', '直刺0．5～1寸，局部酸胀，向手部放散。《甲乙经》卷九“溺黄，肠鸣相逐，下廉主之”。', '', '');
INSERT INTO `xwDetail` VALUES ('10', '三阴交', '4', 'Sānyīnjiāo', 'syj', 'SP6', '足太阴、少阴、厥阴经交会穴', '', '在小腿内侧，当足内踝尖上3寸，胫骨内侧缘后方。', '第1步：正坐或仰卧位；<br>第2步：手4指并拢，小指下边缘紧靠内踝尖上，示指上缘处，小腿内侧骨（胫骨）后方，即为本穴。', '①月经不调，痛经，崩漏，赤白带下，经闭，癥瘕，阴挺，难产，产后血晕，恶露不尽，久不成孕，梦遗，遗精，阳痿，早泄，阴茎痛，疝气，睾丸缩腹；②遗尿，尿闭，水肿，小便不利；③脾胃虚弱，肠鸣，腹胀，泄泻，足痿，脚气，肌肉疼痛；④皮肤病，湿疹，荨麻疹；⑤失眠，头痛头晕，两胁下痛等。', '隔物灸仪艾灸时间：30-90分钟；温度：38-48℃；<br>艾条悬灸时间：10-20分钟；<br>艾炷灸时间：5-9壮。', '①现代常用于治疗急慢性肠炎、细菌性痢疾、功能性子宫出血、遗尿、性功能减退、高血压、神经性皮炎、湿疹、神经衰弱、下肢神经痛或瘫痪等。配天枢、合谷等主治急性肠炎；配中极、行间主治月经不调、痛经；配阴陵泉、膀胱俞、中极主治癃闭。<br>②《针灸资生经》云：妊娠不可刺。', '三阴交漏谷_opt', '5', '①月经不调，痛经，崩漏，赤白带下，经闭，癥瘕，阴挺，难产，产后血晕，恶露不尽，久不成孕，梦遗，遗精，阳痿，早泄，阴茎痛，疝气，睾丸缩腹；②遗尿，尿闭，水肿，小便不利；③脾胃虚弱，肠鸣，腹胀，泄泻，足痿，脚气，肌肉疼痛；④皮肤病，湿疹，荨麻疹；⑤失眠，头痛头晕，两胁下痛等。', '月经不调,不孕症,遗精,阳痿,遗尿,湿疹', '①现代常用于治疗急慢性肠炎、细菌性痢疾、功能性子宫出血、遗尿、性功能减退、高血压、神经性皮炎、湿疹、神经衰弱、下肢神经痛或瘫痪等。配天枢、合谷等主治急性肠炎；配中极、行间主治月经不调、痛经；配阴陵泉、膀胱俞、中极主治癃闭。\r\n②《针灸资生经》云：妊娠不可刺。', '慢性胃炎,急性胃肠炎,痢疾,功能性子宫出血（崩漏）,神经性皮炎,神经衰弱', '隔物灸仪艾灸时间：30-90分钟；温度：38-48℃；\r\n艾条悬灸时间：10-20分钟；\r\n艾炷灸时间：5-9壮。', '自我按摩，用拇指的指腹端轻轻揉按此穴。', '', '', '直刺 l～1．5寸。孕妇禁针。', '', '');
INSERT INTO `xwDetail` VALUES ('11', '上明', '15', 'Shàngmíng', 'sm', '', '', '', '在额部，眉弓中点，眶上缘下。', '额部，眉弓中点，眶上缘下方凹陷处，即为本穴。', '目疾。', '不宜灸。', '', '上明_opt', '9', '目疾。', '', '', '', '不宜灸。', '双手拇指重叠用指腹揉按此穴。', '', '', '轻压眼球向下，向眶缘缓慢直刺0.5~1.5寸，不提插。', '', '');
INSERT INTO `xwDetail` VALUES ('12', '上星', '13', 'Shàngxīng', 'sx', 'DU23', '', '', '在头部，当前发际正中直上1寸。', '第1步：正坐或仰卧位；<br>第2步：从前发际正中直上量1横指处（大拇指指间关节部位的横径为1寸），即为本穴。', '①头痛，眩晕，神经衰弱；②鼻渊，鼻衄，目赤肿痛，迎风流泪，角膜白斑；③疟疾，热病。', '隔物灸仪艾灸时间：20-50分钟；温度：38-48 ℃；<br>艾条悬灸时间：5-10分钟；<br>间接灸时间3-5壮。', '①现代常用于治疗鼻炎、鼻窦炎、神经衰弱等。配百会、囟会、承光主治鼻塞不闻香臭、头痛；配合谷、足三里主治鼻渊、眩晕。<br>②《马氏温灸法》：此穴灸后眼视物明亮，为眼病常用灸穴。', '上星神庭_opt', '10', '①头痛，眩晕，神经衰弱；②鼻渊，鼻衄，目赤肿痛，迎风流泪，角膜白斑；③疟疾，热病。', '头痛,眩晕,神经衰弱,鼻渊,迎风流泪', '①现代常用于治疗鼻炎、鼻窦炎、神经衰弱等。配百会、囟会、承光主治鼻塞不闻香臭、头痛；配合谷、足三里主治鼻渊、眩晕。\r\n②《马氏温灸法》：此穴灸后眼视物明亮，为眼病常用灸穴。', '过敏性鼻炎,鼻塞', '隔物灸仪艾灸时间：20-50分钟；温度：38-48 ℃；\r\n艾条悬灸时间：5-10分钟；\r\n间接灸时间3-5壮。', '用手食指中指的指腹揉按此穴。', '', '', '平刺0.5～0.8寸。《玉龙歌》“鼻流清涕名鼻渊，先泻后补疾可痊。若是头风并眼痛，上星穴内刺勿偏”。', '', '');
INSERT INTO `xwDetail` VALUES ('13', '上髎', '7', 'Shàngliáo', 'sl', 'BL31', '', '', '在骶部，当髂后上棘与后正中线之间，适对第1骶后孔。', '第1步：俯卧；<br>第2步：从骨盆后面髂嵴最高点向内下方骶角两侧循摸可及一高骨突起（即髂后上棘），与之平齐的髂骨正中突起处为第1骶椎；<br>第3步：在第1骶椎旁的凹陷处，即为本穴。', '①月经不调，赤白带下，阴挺等妇科病证；②遗精、阳痿；③大、小便不利，腰骶痛。', '隔物灸仪艾灸时间：30-60分钟；温度：38-50℃；<br>艾条悬灸时间：10-15分钟；<br>艾炷灸时间：5-7壮。', '现代常用于治疗腰骶神经痛、腰骶关节炎、盆腔炎、下肢瘫痪等。配关元主治遗精、阳痿；配血海、气海主治月经不调。', '上髎次髎中髎下髎八髎_opt', '13', '①月经不调，赤白带下，阴挺等妇科病证；②遗精、阳痿；③大、小便不利，腰骶痛。', '月经不调,带下病,遗精,阳痿', '现代常用于治疗腰骶神经痛、腰骶关节炎、盆腔炎、下肢瘫痪等。配关元主治遗精、阳痿；配血海、气海主治月经不调。', '慢性盆腔炎', '隔物灸仪艾灸时间：30-60分钟；温度：38-50℃；\r\n艾条悬灸时间：10-15分钟；\r\n艾炷灸时间：5-7壮。', '自我按摩，用拇指的指腹端揉按此穴。', '', '', '直刺1-1.5寸。《甲乙》“热病汗不出，上髎及孔最主之”，《素问.骨空论》“腰痛不可以转摇，急引引卵，刺八髎与痛上”。', '', '');
INSERT INTO `xwDetail` VALUES ('14', '下关', '3', 'Xiàguān', 'xg', 'ST7', '足阳明、少阳交会穴', '', '在面部耳前方，当颧弓与下颌切迹所形成的凹陷中。', '第1步：正坐或仰卧位；<br>第2步：由耳屏向前1横指可触及一高骨，即为颧弓；<br>第3步：在此高骨的下方有一凹陷处即为本穴，张口该凹陷闭合、突起。', '①下颌关节炎、三叉神经痛、齿痛、口眼歪斜等面口病证；②耳聋、耳鸣、聤耳等耳疾。', '艾条悬灸：10-20分钟。', '①现代常用于治疗颞颌关节炎、牙痛、面神经麻痹、聋哑等。配听宫、合谷主治颞颌关节炎；配颊车、合谷主治牙关紧闭。<br>②《马氏温灸法》：灸上牙痛亦效。', '下关头维_opt', '14', '①下颌关节炎、三叉神经痛、齿痛、口眼歪斜等面口病证；②耳聋、耳鸣、聤耳等耳疾。', '三叉神经痛,牙痛,耳鸣、耳聋,聤耳', '①现代常用于治疗颞颌关节炎、牙痛、面神经麻痹、聋哑等。配听宫、合谷主治颞颌关节炎；配颊车、合谷主治牙关紧闭。\r\n②《马氏温灸法》：灸上牙痛亦效。', '', '艾条悬灸：10-20分钟。', '自我按摩，用食指指腹端轻轻揉按此穴。', '', '', '直刺0．5～l寸。留针时不可作张口动作，以免折针。闪电样感觉传至下颌部或舌部。三叉神经痛可以用30号2.5寸毫针针刺下关穴，进针后，针尖以80度角向下后方，朝对侧乳突方向深刺2寸左右。《金针梅花诗钞》“颊车颊端开口空，牙关紧闭卒中风，口眼歪斜声不出，牙难嚼物颊生红”。', '', '');
INSERT INTO `xwDetail` VALUES ('15', '下巨虚', '3', 'Xiàjùxū', 'xjx', 'ST39', '小肠下合穴', '', '在小腿前外侧，当犊鼻穴下9寸，距胫骨前缘1横指（中指)。', '坐位屈膝。先确定条口位置（参见“条口”），从条口向下量1横指，在胫、腓骨之间可触及一凹陷处，即为本穴。', '①腹泻、痢疾、小腹痛等胃肠病证；②下肢痿痹；③乳痈。', '隔物灸仪艾灸时间：30-60分钟；温度：38-50℃；<br>艾条悬灸时间：10-20分钟；<br>艾炷灸时间：5-9壮。', '现代常用于治疗细菌性痢疾、急慢性肠炎、下肢瘫痪等。配曲池、太白等主治泻痢脓血；配阳陵泉、解溪主治下肢麻木。', '丰隆条口下巨虚_opt', '15', '①腹泻、痢疾、小腹痛等胃肠病证；②下肢痿痹；③乳痈。', '腹泻,痢疾,腹痛', '现代常用于治疗细菌性痢疾、急慢性肠炎、下肢瘫痪等。配曲池、太白等主治泻痢脓血；配阳陵泉、解溪主治下肢麻木。', '急性胃肠炎', '隔物灸仪艾灸时间：30-60分钟；温度：38-50℃；\r\n艾条悬灸时间：10-20分钟；\r\n艾炷灸时间：5-9壮。', '自我按摩，用拇指的指腹端轻轻揉按此穴。或握拳用指间关节叩击此处。', '', '', '直刺1-1.5寸。局部麻胀感向下放散。《灵枢.邪气脏腑病》“小肠病者，小腹痛，腰脊控睾而痛，时窘之后，当耳前热，若脉陷者，此其候也。手太阳病也，取巨虚下廉”。', '', '');
INSERT INTO `xwDetail` VALUES ('16', '下廉', '2', 'Xiàlián', 'xl', 'LI8', '', '', '在前臂背面桡侧，当阳溪与曲池的连线上，肘横纹下4寸。', '伸臂，掌向下。阳溪（参见“阳溪”）与曲池（参见“曲池”）连线上，上1/3与下2/3交界处为取穴部位。', '①肘臂痛；②头痛，眩晕，目痛；③腹胀，腹痛；④急性脑血管病。', '隔物灸仪艾灸时间：30-40分钟；温度：38-45℃；<br>艾条悬灸时间：5-10分钟；<br>艾炷灸时间：3-5壮。', '现代常用于治疗上肢麻木及肿痛、头痛、高血压等。配头维、神庭主治头痛、眩晕、目痛等。', '011偏历温溜下廉_opt', '16', '①肘臂痛；②头痛，眩晕，目痛；③腹胀，腹痛；④急性脑血管病。', '头痛,眩晕,腹胀,腹痛', '现代常用于治疗上肢麻木及肿痛、头痛、高血压等。配头维、神庭主治头痛、眩晕、目痛等。', '', '隔物灸仪艾灸时间：30-40分钟；温度：38-45℃；\r\n艾条悬灸时间：5-10分钟；\r\n艾炷灸时间：3-5壮。', '自我按摩，用拇指远端指腹点按弹拨该穴。', '', '', '直刺0．5～0．8寸，局部酸胀，向手部放散。《甲乙经》卷九“溺黄，下廉主之”。《甲乙经》卷十二“眼痛，下廉主之”', '', '');
INSERT INTO `xwDetail` VALUES ('17', '下极俞', '15', 'Xiàjíshū', 'xjs', 'EX-B5', '', '', '在腰部，当后正中线上，第3腰椎棘突下。', '坐位或俯卧位。取一线过两侧髂前上棘绕腰腹一周，从该线与脊柱交点向上推1个椎体（即第3腰椎），其棘突下缘凹陷处，即为本穴。', '①腰痛，下肢痛；②腹痛、腹泻；③小便不利，遗尿。', '隔物灸仪艾灸时间：30-50 分钟；温度：38-50 ℃；<br>艾条悬灸时间：5-10分钟；<br>艾炷灸时间：3-5壮。', '现代常用于治疗腰骶部病变、下肢瘫痪、肠炎、遗尿等。', '下极俞_opt', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `xwDetail` VALUES ('18', '下髎', '7', 'Xiàliáo', 'xl', 'BL34', '', '', '在骶部，当中髎下内方，适对第4骶后孔。', '第1步：俯卧位；<br>第2步：在腰骶部，沿后正中线向下触摸，可触及一凹陷，即为骶管裂孔；<br>第3步：在骶管裂孔两侧可触及一凹陷，即为本穴。', '①腹痛、便秘；②小便不利；③带下；④腰骶痛。', '隔物灸仪艾灸时间：30-60分钟；温度：38-50℃；<br>艾条悬灸时间：10-15分钟；<br>艾炷灸时间：5-7壮。', '现代常用于治疗盆腔炎、泌尿系感染、性功能障碍等。配足三里、天枢主治泄泻；配风市、昆仑主治腰痛。', '上髎次髎中髎下髎八髎_opt', '19', '①腹痛、便秘；②小便不利；③带下；④腰骶痛。', '腹痛,便秘,小便不利,带下病', '现代常用于治疗盆腔炎、泌尿系感染、性功能障碍等。配足三里、天枢主治泄泻；配风市、昆仑主治腰痛。', '慢性盆腔炎,腰痛', '隔物灸仪艾灸时间：30-60分钟；温度：38-50℃；\r\n艾条悬灸时间：10-15分钟；\r\n艾炷灸时间：5-7壮。', '自我按摩，用拇指的指腹端揉按此穴。', '', '', '直刺1-1.5寸。《百症赋》“寒湿湿热下髎定”，《大成》卷六“主腰不得转，女子下苍汁不禁”。', '', '');
INSERT INTO `xwDetail` VALUES ('19', '不容', '3', 'Bùróng', 'br', 'ST19', '', '', '在上腹部，当脐中上6寸，距前正中线2寸。', '第1步：仰卧位；<br>第2步：沿前正中线向下触摸，找出胸骨体与剑突间形成的凹陷，即胸剑联合；<br>第3步：将胸剑联合到脐中的连线分为4等分，从上1/4与下3/4交点处左一水平线；<br>第4步：该线与乳中线（过乳头的垂直线）有一交点；<br>第5步：两个交点的中间处，即为本穴。', '①胃痛、呕吐、纳少、腹胀等胃疾；②肋间神经痛，肩臂部诸肌痉挛或萎缩。', '隔物灸仪艾灸时间：30-50分钟；温度：38-50℃；<br>艾条悬灸时间：5-10分钟；<br>艾炷灸时间：3-5壮。', '①现代常用于治疗胃炎、胃或十二指肠溃疡、胃下垂等。配中脘、公孙主治胃炎、胃溃疡。<br>②《马氏温灸法》：治各种胃病，包括胃癌。', '不容承满梁门_opt', '20', '①胃痛、呕吐、纳少、腹胀等胃疾；②肋间神经痛，肩臂部诸肌痉挛或萎缩。', '呕吐,腹胀', '①现代常用于治疗胃炎、胃或十二指肠溃疡、胃下垂等。配中脘、公孙主治胃炎、胃溃疡。\r\n②《马氏温灸法》：治各种胃病，包括胃癌。', '慢性胃炎,胃下垂', '隔物灸仪艾灸时间：30-50分钟；温度：38-50℃；\r\n艾条悬灸时间：5-10分钟；\r\n艾炷灸时间：3-5壮。', '自我按摩，用食中指的指腹端轻轻揉按此穴。也可采用王雅儒《脏腑图点穴：磁圆梅针点叩此处》手法操作。', '', '', '直刺0．5～0．8寸。过饱者禁针，肝肿大者慎针或禁针，不宜作大幅度提插。《千金》“不容、期门，主心切痛噫酸”。', '', '');
INSERT INTO `xwDetail` VALUES ('20', '下脘', '14', 'Xiàwǎn', 'xw', 'RN10', '任脉、足太阴经交会穴', '', '在上腹部，前正中线上，当脐中上2寸。', '第1步：仰卧位；<br>第2步：沿前正中线向下触摸，找出胸骨体与剑突间行成的凹陷，即胸剑联合；<br>第3步：将胸剑联合与脐中连线分为4等分；<br>第4步：该连线的下1/4与上3/4交点处，即为本穴。', '①腹痛、腹胀、食谷不化、呃逆、腹泻等胃肠病证；②痞块。', '隔物灸仪艾灸时间：30-70分钟；温度：38-50 ℃；<br>艾条悬灸时间：10-20分钟；<br>艾炷灸时间：5-7壮。', '现代常用于治疗胃炎、胃溃疡、肠炎、痢疾等。灸下脘、天枢、照海主治痢疾。里急后重；配中脘主治腹坚硬胀、痞块。', '水分下脘建里中脘_opt', '18', '①腹痛、腹胀、食谷不化、呃逆、腹泻等胃肠病证；②痞块。', '腹痛,腹胀,呃逆,腹泻,腹内痞块', '现代常用于治疗胃炎、胃溃疡、肠炎、痢疾等。灸下脘、天枢、照海主治痢疾。里急后重；配中脘主治腹坚硬胀、痞块。', '慢性胃炎,痢疾', '隔物灸仪艾灸时间：30-70分钟；温度：38-50 ℃；\r\n艾条悬灸时间：10-20分钟；\r\n艾炷灸时间：5-7壮。', '用手食指中指的指腹揉按此穴。', '', '', '直刺0.5-1寸；《甲乙》卷九“食欲不化，入腹还出，下脘主之”，《千金》“凡饮食不化，入腹还出，先去下脘，后取三里泻之”，《大成》卷八“翻胃，先取下脘，后取三里（泻），肾俞、膈俞（百壮），中脘、脾俞”。', '', '');
INSERT INTO `xwDetail` VALUES ('21', '丝竹空', '10', 'Sīzhúkōng', 'szk', 'SJ23', '', '', '在面部，当眉梢凹陷处。', '正坐或侧卧位。手指沿眉毛走形从内向外后推，至眉梢处可触及一凹陷处，按压有酸胀感，即为本穴。', '①目赤肿痛，眼睑瞤动；②头痛，齿痛；③癫痫。', '禁灸。', '现代常用于治疗面神经麻痹、面肌痉挛、结膜炎等。配瞳子髎、睛明、攒竹主治目赤肿痛；配太阳、外关主治偏头痛；配足通谷、太冲主治癫痫。', '135丝竹空_opt', '22', '①目赤肿痛，眼睑瞤动；②头痛，齿痛；③癫痫。', '头痛,牙痛,癫痫', '现代常用于治疗面神经麻痹、面肌痉挛、结膜炎等。配瞳子髎、睛明、攒竹主治目赤肿痛；配太阳、外关主治偏头痛；配足通谷、太冲主治癫痫。', '面肌痉挛,急性结膜炎,偏头痛', '禁灸。', '自我按摩，用手拇指或食指的指腹端轻轻揉按此穴。', '', '', '平刺0.3～0.5寸。配合阳白、攒竹治眼睑下垂有效。因痛而痉，致其上抬。', '', '');
INSERT INTO `xwDetail` VALUES ('22', '丘墟', '11', 'Qiūxū', 'qx', 'GB40', '原穴', '', '在足外踝的前下方，当趾长伸肌腱的外侧凹陷处。', '坐位或仰卧位。取足外踝前缘垂线与下缘水平线的交点，按压有凹陷处，即为本穴。', '①胸胁胀痛；②下肢麻痹，脚气，外踝肿痛；③疟疾，疝气；④中风偏瘫。', '隔物灸仪艾灸时间：30-70分钟；温度：38-50℃；<br>艾条悬灸时间：5-10分钟；<br>艾炷灸时间：3-5壮。', '现代常用于治疗踝关节及周围软组织疾病、胆囊炎等。配昆仑、申脉主治外踝肿痛；配阳陵泉、期门主治胸胁胀痛。', '悬钟丘墟_opt', '21', '①胸胁胀痛；②下肢麻痹，脚气，外踝肿痛；③疟疾，疝气；④中风偏瘫。', '中风偏瘫', '现代常用于治疗踝关节及周围软组织疾病、胆囊炎等。配昆仑、申脉主治外踝肿痛；配阳陵泉、期门主治胸胁胀痛。', '胆囊炎、胆石症', '隔物灸仪艾灸时间：30-70分钟；温度：38-50℃；\r\n艾条悬灸时间：5-10分钟；\r\n艾炷灸时间：3-5壮。', '自我按摩，用手食指或中指的指腹端揉按此穴。', '', '', '直刺1-1.5寸，丘墟透照海1.5-2寸交叉取穴，治疗胸胁痛效果颇佳。', '', '');
INSERT INTO `xwDetail` VALUES ('23', '中冲', '9', 'Zhōngchōng', 'zc', 'PC9', '井穴', '', '在手中指末节尖端中央。', '俯掌。在手中指尖端的中央取穴。', '①昏迷、中暑、昏厥等急症；②心痛；③小儿夜啼，舌强肿痛。', '艾条悬灸：5-10分钟；<br>艾炷灸时间：1-3壮；麦粒灸：1-3壮。', '现代常用于治疗昏迷、中暑、心绞痛等。配水沟、太冲、劳宫、曲泽主治中风昏迷、舌强不语；指压中冲用于心绞痛的应急治疗；配大椎、曲池、曲泽主治中暑；配大椎、合谷、外关主治小儿惊风。', '劳宫中冲_opt', '23', '①昏迷、中暑、昏厥等急症；②心痛；③小儿夜啼，舌强肿痛。', '小儿夜啼症', '现代常用于治疗昏迷、中暑、心绞痛等。配水沟、太冲、劳宫、曲泽主治中风昏迷、舌强不语；指压中冲用于心绞痛的应急治疗；配大椎、曲池、曲泽主治中暑；配大椎、合谷、外关主治小儿惊风。', '心绞痛,小儿惊风', '艾条悬灸：5-10分钟；\r\n艾炷灸时间：1-3壮；麦粒灸：1-3壮。', '自我按摩，用手拇指掐点此穴。', '', '', '浅刺0.1寸；或点刺出血。为急救要穴之一。', '', '');
INSERT INTO `xwDetail` VALUES ('24', '中封', '12', 'Zhōngfēng', 'zf', 'LR4', '经穴', '', '在足背侧，当足内踝前，商丘与解溪连线之间，胫骨前肌腱的内侧凹陷处。', '第1步：坐位或仰卧位，拇趾上跷；<br>第2步：足背内侧可见一大筋（胫骨前肌腱），在其内侧、足内踝前下方可触及一凹陷处，即为本穴。', '①疝气，腹痛；②遗精；③小便不利；④下肢痿痹，足踝肿痛；⑤腹胀，纳差，肝炎黄疸。', '隔物灸仪艾灸时间：30-50分钟；温度：38-48℃；<br>艾条悬灸时间：5-10分钟；<br>艾炷灸时间：3-5壮。', '现代常用于治疗疝气、遗精、膀胱炎、肝炎、踝关节扭伤等。配解溪、昆仑主治足踝肿痛；配大赫、志室主治遗精。', '太冲中封_opt', '24', '①疝气，腹痛；②遗精；③小便不利；④下肢痿痹，足踝肿痛；⑤腹胀，纳差，肝炎黄疸。', '腹痛,遗精,小便不利,腹胀,慢性肝炎,黄疸', '现代常用于治疗疝气、遗精、膀胱炎、肝炎、踝关节扭伤等。配解溪、昆仑主治足踝肿痛；配大赫、志室主治遗精。', '膀胱炎', '隔物灸仪艾灸时间：30-50分钟；温度：38-48℃；\r\n艾条悬灸时间：5-10分钟；\r\n艾炷灸时间：3-5壮。', '自我按摩，用手食指或中指的指腹端揉按此穴。', '', '', '斜刺0.5-1寸，《甲乙》卷十二“女子少腹大，乳难......嗜干，中封主之”。', '', '');
INSERT INTO `xwDetail` VALUES ('25', '中府', '1', 'Zhōngfǔ', 'zf', 'LU1', '肺募穴，手、足太阴交会穴', '', '在胸前壁的外上方，云门下1寸，平第1肋间隙，距前正中线6寸。', '第1步：正立或正坐位，双手叉腰；<br>第2步：在锁骨外侧下缘的三角窝处即是云门穴；<br>第3步：从此窝正中垂直向下量1横指（大拇指指间关节部位的横径为1寸）处即为本穴。', '①咳嗽、气喘、胸满痛等肺部病症；②肩背痛。', '隔物灸仪艾灸时间：30-50分钟；温度：38-45℃；<br>艾条悬灸时间：5-10分钟；<br>艾炷灸时间：5-7壮。', '①现代常用于治疗气管炎、支气管哮喘、肺炎等。配肺俞主治外感和内伤咳嗽；配复溜主治肺阴虚之干咳、肺痨等。<br>②《马氏温灸法》：治肺结核等各种肺病，亦用于下肢水肿。', '002云门中府_opt', '25', '①咳嗽、气喘、胸满痛等肺部病症；②肩背痛。', '咳嗽,气喘', '①现代常用于治疗气管炎、支气管哮喘、肺炎等。配肺俞主治外感和内伤咳嗽；配复溜主治肺阴虚之干咳、肺痨等。\r\n②《马氏温灸法》：治肺结核等各种肺病，亦用于下肢水肿。', '支气管炎,支气管哮喘,肺炎,肺结核', '隔物灸仪艾灸时间：30-50分钟；温度：38-45℃；\r\n艾条悬灸时间：5-10分钟；\r\n艾炷灸时间：5-7壮。', '自我按摩，食指放在中指上，用中指指腹点按揉运穴位的酸胀处3分钟。', '', '', '向外斜刺0．5～0．8寸，进针宜用指切手法，不可向内深刺，以免伤及肺脏、引起气胸，甚至窒息。局部有酸胀感。《素问.禁刺论》“刺膺中陷中肺，为喘逆仰息”。\r\n临床实践证明，本穴位除能治疗本经咳喘、胸部胀满外，亦可以治疗脾失健运而导致的纳差、腹胀等脾经病症。另外，本穴对诊断肺结核有一定的参考价值，并常作为治疗肺结核病的主穴之一。', '', '');
INSERT INTO `xwDetail` VALUES ('26', '中庭', '14', 'Zhōngtíng', 'zt', 'RN16', '', '', '在胸部，前正中线上，平第5肋间，即胸剑结合部。', '仰卧位或正坐。胸部前正中线上胸骨体与剑突间（即胸剑结合部）可触及一凹陷，即为本穴。', '①胸胁支满；②噎膈，呕吐，小儿吐乳。', '隔物灸仪艾灸时间：30-40 分钟；温度：38-48 ℃；<br>艾条悬灸时间：5-10分钟；<br>艾炷灸时间：3-5壮。', '现代常用于治疗食道炎、食道狭窄、贲门痉挛等。配中府主治噎膈、停食、食反、胸闷；配俞府、意舍主治呕吐、食不化。', '上脘巨阙鸠尾中庭_opt', '26', '①胸胁支满；②噎膈，呕吐，小儿吐乳。', '噎膈,呕吐', '现代常用于治疗食道炎、食道狭窄、贲门痉挛等。配中府主治噎膈、停食、食反、胸闷；配俞府、意舍主治呕吐、食不化。', '胸闷', '隔物灸仪艾灸时间：30-40 分钟；温度：38-48 ℃；\r\n艾条悬灸时间：5-10分钟；\r\n艾炷灸时间：3-5壮。', '用手食指中指的指腹揉按此穴。', '', '', '平刺0.3～0.5寸。', '', '');
INSERT INTO `xwDetail` VALUES ('27', '中极', '14', 'Zhōngjí', 'zj', 'RN3', '膀胱募穴，任脉、足三阴经交会穴', '', '在下腹部，前正中线上，当脐中下4寸。', '第1步：仰卧位或正坐位；<br>第2步：确定耻骨联合：沿下腹部前正中线垂直向下推，可触及一骨头，此骨头即为耻骨联合；<br>第3步：将脐中与耻骨联合上缘中点的连线平分为5等分；<br>第4步：该连线的上4/5与下1/5交点处即为本穴。', '①痛经、月经病 ；②子宫内膜炎、盆腔炎、膀胱炎； ③男子性功能障碍、尿潴留、前列腺炎等。', '隔物灸仪艾灸时间：30-70分钟；温度：38-50 ℃；<br>艾条悬灸时间：5-10分钟；<br>艾炷灸时间：5-9壮。', '①现代常用于治疗痛经、月经病、子宫内膜炎、盆腔炎、膀胱炎、男子性功能障碍、尿潴留、前列腺炎等。配地机、次髎主治痛经；配关元、三阴交主治遗精、阳痿；中极透曲骨，配三阴交、地机主治癃闭。<br>②《马氏温灸法》：卵巢肿瘤、睾丸炎、肾炎。', '曲骨中极关元石门_opt', '27', '①痛经、月经病 ；②子宫内膜炎、盆腔炎、膀胱炎； ③男子性功能障碍、尿潴留、前列腺炎等。', '痛经,慢性盆腔炎,膀胱炎,尿潴留（癃闭）,前列腺炎', '①现代常用于治疗痛经、月经病、子宫内膜炎、盆腔炎、膀胱炎、男子性功能障碍、尿潴留、前列腺炎等。配地机、次髎主治痛经；配关元、三阴交主治遗精、阳痿；中极透曲骨，配三阴交、地机主治癃闭。\r\n②《马氏温灸法》：卵巢肿瘤、睾丸炎、肾炎。', '遗精,阳痿,睾丸炎,肾炎', '隔物灸仪艾灸时间：30-70分钟；温度：38-50 ℃；\r\n艾条悬灸时间：5-10分钟；\r\n艾炷灸时间：5-9壮。', '用手食指中指的指腹揉按此穴。', '', '', '直刺1～2寸；局部酸胀，可放射至外生殖器及会阴部位。孕妇慎用。穴位在腹部白线上，深部为乙状结肠。，《入门》“中极主妇人下元虚冷，月事不调，赤白带下”。', '', '');
INSERT INTO `xwDetail` VALUES ('28', '中枢', '13', 'Zhōngshū', 'zs', 'DU7', '', '', '在背部，当后正中线上，第10胸椎棘突下凹陷中。', '正坐或俯卧位。从两侧肩胛下角连线与后正中线相交处所在椎体为第7胸椎，垂直向下推3个椎体（即第10胸椎），该椎体棘突下缘凹陷处，即为本穴。', '①黄疸、呕吐、腹满、胃痛等脾胃病证；③腰背疼痛。', '隔物灸仪艾灸时间：30-50 分钟；温度：38-48 ℃；<br>艾条悬灸时间：10-20分钟；<br>艾炷灸时间：5-7壮。', '现代常用于治疗胃肠痉挛、胃炎、肠炎等。配中脘、足三里主治腹满不欲食、胸腹冷痛。', '脊中中枢_opt', '28', '①黄疸、呕吐、腹满、胃痛等脾胃病证；③腰背疼痛。', '黄疸,呕吐,腹满', '现代常用于治疗胃肠痉挛、胃炎、肠炎等。配中脘、足三里主治腹满不欲食、胸腹冷痛。', '慢性胃炎,急性胃肠炎', '隔物灸仪艾灸时间：30-50 分钟；温度：38-48 ℃；\r\n艾条悬灸时间：10-20分钟；\r\n艾炷灸时间：5-7壮。', '用手食指中指的指腹揉按此穴。', '', '', '斜刺0.5-1寸。', '', '');
INSERT INTO `xwDetail` VALUES ('29', '中泉', '15', 'Zhōngquán', 'zq', 'EX-UE3', '', '', '在腕背侧横纹中，当指总伸肌腱桡侧的凹陷处。', '第1步：抬臂，掌心向下；<br>第2步：在腕背横纹上，指总伸肌腱（指示、中、环、小指四个手指的肌腱）的拇指侧（桡侧）凹陷中，即为本穴。', '①胸闷、心痛、咳嗽、气喘；②胃痛；③掌中热。', '隔物灸仪艾灸时间：20-30 分钟；温度：38-45 ℃；<br>艾条悬灸时间：5-10分钟；<br>艾炷灸时间：3-5壮。', '现代常用于治疗心绞痛、支气管炎、胃炎等。', '外劳宫腰痛点中泉_opt', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `xwDetail` VALUES ('30', '中注', '8', 'Zhōngzhù', 'zz', 'KI15', '足少阴、冲脉交会穴', '', '在下腹部，当脐中下1寸，前正中线旁开0.5寸。', '仰卧位。从肚脐向下量1横指，再自前正中线旁开量半横指，按压有酸胀感处，即为本穴。', '①月经不调；②腹痛、便秘、泄泻等胃肠病证。', '隔物灸仪艾灸时间：30-50分钟；温度：38-45℃；<br>艾条悬灸时间：10-15分钟；<br>艾炷灸时间：3-5壮。', '现代常用于治疗月经不调、附件炎、睾丸炎等。配三阴交、次髎主治月经不调。', '四满中注肓俞_opt', '30', '①月经不调；②腹痛、便秘、泄泻等胃肠病证。', '月经不调,腹痛,便秘', '现代常用于治疗月经不调、附件炎、睾丸炎等。配三阴交、次髎主治月经不调。', '睾丸炎', '隔物灸仪艾灸时间：30-50分钟；温度：38-45℃；\r\n艾条悬灸时间：10-15分钟；\r\n艾炷灸时间：3-5壮。', '自我按摩，用手拇指或食指的指腹端轻轻揉按此穴。', '', '', '直刺1-1.5寸，局部麻胀。', '', '');
INSERT INTO `xwDetail` VALUES ('31', '中渎', '11', 'Zhōngdú', 'zd', 'GB32', '', '', '在大腿外侧，当风市下2寸，或腘横纹上5寸，股外侧肌与股二头肌之间。', '直立或坐位。从风市（参见“风市”）直下量2横指（拇指）处，在两筋之间按压有酸胀感，即为本穴。', '下肢痿痹，坐骨神经痛，膝关节炎，腓肠肌痉挛。', '隔物灸仪艾灸时间：30-40分钟；温度：38-48℃；<br>艾条悬灸时间：10-20分钟；<br>艾炷灸时间：3-5壮。', '现代常用于治疗下肢瘫痪等。配环跳、阳陵泉、足三里主治下肢痿痹。', '风市中渎膝阳关_opt', '31', '下肢痿痹，坐骨神经痛，膝关节炎，腓肠肌痉挛。', '坐骨神经痛,腓肠肌痉挛', '现代常用于治疗下肢瘫痪等。配环跳、阳陵泉、足三里主治下肢痿痹。', '', '隔物灸仪艾灸时间：30-40分钟；温度：38-48℃；\r\n艾条悬灸时间：10-20分钟；\r\n艾炷灸时间：3-5壮。', '自我按摩，用手食指或中指的指腹端揉按此穴。', '', '', '直刺1-2寸。', '', '');
INSERT INTO `xwDetail` VALUES ('32', '中渚', '10', 'Zhōngzhǔ', 'zz', 'SJ3', '输穴', '', '在手背部，当第4掌指关节的后方，第4、5掌骨间凹陷处。', '第1步：掌心向下；<br>第2步：在手背部第4、5指指缝间，掌指关节后可触及一凹陷，即为本穴。', '①头痛，目赤，耳鸣，耳聋，喉痹等头面五官疾患；②肩、背、肘、臂疼痛麻木，手指不能屈伸；③热病；④疟疾。', '隔物灸仪艾灸时间：30-50分钟；温度：38-48℃；<br>艾条悬灸时间：5-10分钟；<br>艾炷灸时间：3-5壮。', '现代常用于治疗头痛、神经性耳聋、美尼尔综合征、眶上神经痛、肩周炎、急慢性腰痛等。配听宫、翳风主治耳鸣、头痛；配肩髃、曲池、外关治疗肩臂肘酸痛；配八邪、外关主治手指不能屈伸；配外关、期门主治肋间神经痛。', '阳池中渚液门_opt', '32', '①头痛，目赤，耳鸣，耳聋，喉痹等头面五官疾患；②肩、背、肘、臂疼痛麻木，手指不能屈伸；③热病；④疟疾。', '头痛,耳鸣、耳聋', '现代常用于治疗头痛、神经性耳聋、美尼尔综合征、眶上神经痛、肩周炎、急慢性腰痛等。配听宫、翳风主治耳鸣、头痛；配肩髃、曲池、外关治疗肩臂肘酸痛；配八邪、外关主治手指不能屈伸；配外关、期门主治肋间神经痛。', '肩周炎,腰痛', '隔物灸仪艾灸时间：30-50分钟；温度：38-48℃；\r\n艾条悬灸时间：5-10分钟；\r\n艾炷灸时间：3-5壮。', '', '', '', '', '', '');
INSERT INTO `xwDetail` VALUES ('33', '中脘', '14', 'Zhōngwǎn', 'zw', 'RN12', '胃募穴；八会穴之腑会；任脉、手太阳、少阳', '', '在上腹部，前正中线上，当脐中上4寸。', '第1步：仰卧位；<br>第2步：沿前正中线向下触摸，找出胸骨体与剑突间行成的凹陷，即胸剑联合；<br>第3步：胸剑联合与脐中连线的中点，即为本穴。', '①胃痛、腹胀、呃逆、吞酸、泄泻、黄疸等脾胃病证；②癫狂，失眠，脏躁；③子宫脱垂，荨麻疹，食物中毒。', '隔物灸仪艾灸时间：40-200 分钟；温度：38-52 ℃；<br>艾条悬灸时间：10-20分钟；<br>艾炷灸时间：5-9壮。', '①现代常用于治疗胃炎、胃痉挛、胃溃疡、胃下垂、食物中毒、癫痫、精神病、神经衰弱等。配天枢、足三里、内庭主治霍乱吐泻；配足三里主治脘腹胀痛。<br>②《马氏温灸法》：中脘的主治已不胜枚举，简言之，灸中脘有清除胃肠瘀滞，开启、强壮脾胃之效用。肠胃清则五脏六腑之瘀滞有倾泄之途，脾胃健则五脏六腑生化有源。因此中脘实为调理一切慢性病之必要灸穴，急症亦多用。<br>《针灸资生经》：凡脾疼不可忍，饮食全不进者，皆宜灸。', '水分下脘建里中脘_opt', '33', '①胃痛、腹胀、呃逆、吞酸、泄泻、黄疸等脾胃病证；②癫狂，失眠，脏躁；③子宫脱垂，荨麻疹，食物中毒。', '腹胀,呃逆,吞酸,黄疸,失眠,脏躁', '①现代常用于治疗胃炎、胃痉挛、胃溃疡、胃下垂、食物中毒、癫痫、精神病、神经衰弱等。配天枢、足三里、内庭主治霍乱吐泻；配足三里主治脘腹胀痛。\r\n②《马氏温灸法》：中脘的主治已不胜枚举，简言之，灸中脘有清除胃肠瘀滞，开启、强壮脾胃之效用。肠胃清则五脏六腑之瘀滞有倾泄之途，脾胃健则五脏六腑生化有源。因此中脘实为调理一切慢性病之必要灸穴，急症亦多用。\r\n《针灸资生经》：凡脾疼不可忍，饮食全不进者，皆宜灸。', '慢性胃炎,胃痉挛,胃下垂,癫痫,神经衰弱', '隔物灸仪艾灸时间：40-200 分钟；温度：38-52 ℃；\r\n艾条悬灸时间：10-20分钟；\r\n艾炷灸时间：5-9壮。', '用手食指中指的指腹揉按此穴。', '', '', '直刺1～1.5寸。针刺的方向应该朝下，使针感自上而下。中气下陷的头晕及食道疾患应向上斜刺，使针感下向上传导。中脘穴在腹白线上，深部为幽门部位。其深部有胃、胰腺、腹主动脉，直刺不宜过深，瘦弱的病人尤宜谨慎，肝脾肿大的患者不能向左右侧上方透刺。《甲乙》卷九“心疼身寒，难以俯仰，心疝冲胃，死不知人，中脘主之。腹胀不通，寒中伤饱，饮食不化，中脘主之。小肠有热，溺黄赤，中脘主之”。', '', '');
INSERT INTO `xwDetail` VALUES ('34', '中膂俞', '7', 'Zhōnglǚshū', 'zls', 'BL29', '', '', '在骶部，当骶正中嵴旁1.5寸，平第3骶后孔。', '俯卧位。从骨盆后面髂嵴最高点向内下方骶角两侧循摸可触及一高骨凸起（即髂后上棘），与之平齐的髂骨正中突起处是第1骶椎棘突，向下推2个椎体，为第3骶椎，从该椎旁开量2横指，按压有酸胀感处，即为本穴。', '①腹泻；②疝气；③腰骶痛。', '隔物灸仪艾灸时间：30-40分钟；温度：38-50℃；<br>艾条悬灸时间：10-15分钟；<br>艾炷灸时间：5-7壮。', '现代常用于治疗腰肌劳损、坐骨神经痛、肠炎等。配委中主治腰骶疼痛；配合谷、足三里主治痢疾。', '089膀胱俞中膂俞白环俞_opt', '34', '①腹泻；②疝气；③腰骶痛。', '腹泻', '现代常用于治疗腰肌劳损、坐骨神经痛、肠炎等。配委中主治腰骶疼痛；配合谷、足三里主治痢疾。', '腰肌劳损,坐骨神经痛,急性胃肠炎,痢疾', '隔物灸仪艾灸时间：30-40分钟；温度：38-50℃；\r\n艾条悬灸时间：10-15分钟；\r\n艾炷灸时间：5-7壮。', '自我按摩，用拇指的指腹端揉按此穴。', '', '', '直刺1-1.5寸。《甲乙》卷九“腰痛，不可以俯仰，中膂内俞主之”。', '', '');
INSERT INTO `xwDetail` VALUES ('35', '中都', '12', 'Zhōngdū', 'zd', 'LR6', '郄穴', '', '在小腿内侧，当足内踝尖上7寸，胫骨内侧面的中央。', '坐位或仰卧位。取一标有二等分线的弹性皮筋，将皮筋的两端分别与内踝尖及胫骨内侧髁（膝关节上下、小腿上端内侧的骨性膨大）对齐拉紧，皮筋中点稍下方（约0.5寸）处，即为本穴。', '①疝气、小腹痛；②崩漏，恶露不尽；③胁痛，腹胀；④下肢痿痹。', '隔物灸仪艾灸时间：30-50分钟；温度：38-48℃；<br>艾条悬灸时间：5-10分钟。', '现代常用于治疗功能性子宫出血、膝关节炎等。配归来、太冲主治疝气。', '中都蠡沟_opt', '35', '①疝气、小腹痛；②崩漏，恶露不尽；③胁痛，腹胀；④下肢痿痹。', '腹痛,功能性子宫出血（崩漏）,腹胀', '现代常用于治疗功能性子宫出血、膝关节炎等。配归来、太冲主治疝气。', '膝关节炎', '隔物灸仪艾灸时间：30-50分钟；温度：38-48℃；\r\n艾条悬灸时间：5-10分钟。', '自我按摩，用手食指或中指的指腹端揉按此穴。', '', '', '平刺0.5～0.8寸。', '', '');
INSERT INTO `xwDetail` VALUES ('36', '中髎', '7', 'Zhōngliáo', 'zl', 'BL33', '', '', '在骶部，当次髎下内方，适对第3骶后孔。', '第1步：俯卧位；<br>第2步：确定髂后上棘：骨盆后，从髂嵴最高点向内下方骶角两侧循摸一高骨突起，即是髂后上棘；<br>第3步：与髂后上棘平齐，髂骨正中突起处是第1骶椎棘突；<br>第4步：确定次髎穴：从第1骶椎棘突垂直向下推1个椎体即为第2骶椎棘突，该棘突与髂后上棘间的空隙即第2骶后孔，为次髎穴；<br>第5步：确定骶管裂孔：在尾骨上方，两骶角之间即为骶管裂孔；<br>第6步：把中指按在第2骶后孔处，小指按在骶管裂孔旁凹陷处，示、中、环、小指等距离分开，无名指尖端处，即为本穴。', '①月经不调，白带；②小便不利；③便秘，泄泻；④腰骶痛。', '隔物灸仪艾灸时间：30-60分钟；温度：38-50℃；<br>艾条悬灸时间：10-15分钟；<br>艾炷灸时间：5-7壮。', '现代常用于治疗月经不调、性功能障碍、泌尿系感染等。配中极、曲骨主治小便不利；配关元俞、三阴交主治月经不调、带下。', '上髎次髎中髎下髎八髎_opt', '36', '①月经不调，白带；②小便不利；③便秘，泄泻；④腰骶痛。', '月经不调,白带,小便不利,便秘', '现代常用于治疗月经不调、性功能障碍、泌尿系感染等。配中极、曲骨主治小便不利；配关元俞、三阴交主治月经不调、带下。', '带下病', '隔物灸仪艾灸时间：30-60分钟；温度：38-50℃；\r\n艾条悬灸时间：10-15分钟；\r\n艾炷灸时间：5-7壮。', '自我按摩，用拇指的指腹端揉按此穴。', '', '', '直刺1-1.5寸。《胜玉歌》“腰痛中空穴最奇”。', '', '');
INSERT INTO `xwDetail` VALUES ('37', '中魁', '15', 'Zhōngkúi', 'zk', 'EX-UE4', '', '', '握拳，掌心向下。在中指背侧近侧指间关节的中点处。', '抬臂俯掌。中指背侧第1指间关节（近侧指间关节）横纹中点处，即为本穴。', '①牙痛，鼻出血；②噎膈，反胃，呕吐。', '艾条悬灸：5-10分钟；<br>艾炷灸时间：3-5壮。', '现代常用于治疗齿痛、食道炎 、食道狭窄、贲门痉挛等。', '中魁八邪大骨空小骨空_opt', '37', '①牙痛，鼻出血；②噎膈，反胃，呕吐。', '牙痛,鼻出血,噎膈,反胃,呕吐', '现代常用于治疗齿痛、食道炎 、食道狭窄、贲门痉挛等。', '', '艾条悬灸：5-10分钟；\r\n艾炷灸时间：3-5壮。', '', '', '', '针刺0.2~0.3寸。', '', '');
INSERT INTO `xwDetail` VALUES ('38', '丰隆', '3', 'Fēnglóng', 'fl', 'ST40', '络穴', '', '在小腿前外侧，当外踝尖上8寸，条口外，距胫骨前缘2横指（中指)。', '第1步：正坐屈膝；<br>第2步：先确定腘横纹端与外踝尖连线中点（腘横纹与外踝尖连线的距离为16寸）；<br>第3步：再从胫骨前缘沿该中点水平线往外量2横指（中指）处，在腓骨略前方肌肉丰满处，按压有沉重感，即为本穴。', '①头痛、眩晕；②咳嗽痰多等痰饮病证；③癫狂；④下肢痿痹；⑤高血压，脑出血，脑血管病后遗症；⑥肝炎，阑尾炎，便秘。', '隔物灸仪艾灸时间：30-60分钟；温度：38-52℃；<br>艾条悬灸时间：10-20分钟；<br>艾炷灸时间：5-7壮。', '现代常用于治疗耳源性眩晕、高血压、神经衰弱、精神分裂症、支气管炎、腓肠肌痉挛、肥胖症等。配阴陵泉、商丘、足三里治疗痰湿诸证；配肺俞、尺泽治疗咳嗽痰多。', '丰隆条口下巨虚_opt', '38', '①头痛、眩晕；②咳嗽痰多等痰饮病证；③癫狂；④下肢痿痹；⑤高血压，脑出血，脑血管病后遗症；⑥肝炎，阑尾炎，便秘。', '头痛,眩晕,咳嗽,慢性肝炎,阑尾炎,便秘', '现代常用于治疗耳源性眩晕、高血压、神经衰弱、精神分裂症、支气管炎、腓肠肌痉挛、肥胖症等。配阴陵泉、商丘、足三里治疗痰湿诸证；配肺俞、尺泽治疗咳嗽痰多。', '神经衰弱,支气管炎,腓肠肌痉挛,肥胖症', '隔物灸仪艾灸时间：30-60分钟；温度：38-52℃；\r\n艾条悬灸时间：10-20分钟；\r\n艾炷灸时间：5-7壮。', '自我按摩，用拇指的指腹端轻轻揉按此穴。或握拳用指间关节叩击此处。', '', '', '直刺1-1.5寸。', '', '');
INSERT INTO `xwDetail` VALUES ('39', '乳中', '3', 'Rǔzhōng', 'rz', 'ST17', '', '', '在胸部，当第4肋间隙，乳头中央，距前正中线4寸。', '正坐或仰卧。乳头所在处，即为本穴。', '', '', '本穴不针不灸，只作胸腹部腧穴的定位标志。', '膺窗乳中乳根_opt', '39', '本穴不针不灸，只作胸腹部腧穴的定位标志。', '', '本穴不针不灸，只作胸腹部腧穴的定位标志。', '', '', '', '', '', '《甲乙经》卷三“乳中，禁不可刺灸，灸刺不幸生蚀疮，疮中有脓血清汁者可活，疮中有息肉若蚀疮者死”。', '', '');
INSERT INTO `xwDetail` VALUES ('40', '乳四穴', '15', 'Rǔsìxué', 'rsx', '', '', '', '在以乳头为中心的垂直线和水平线上，分别距乳头2寸。', '', '乳腺疾病。', '隔物灸仪艾灸时间：20-30分钟；温度：38-45 ℃；<br>艾条悬灸时间：10-20分钟。', '', '乳四穴_opt', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `xwDetail` VALUES ('41', '乳根', '3', 'Rǔgēn', 'rg', 'ST18', '', '', '在胸部，当乳头直下，乳房根部，第5肋间隙，距前正中线4寸。', '第1步：正坐或仰卧位；<br>第2步：从乳头沿乳中线（过乳头的垂直线）向下推1个肋间隙（即第5肋间隙），按压有酸胀感处，即为本穴。', '①乳痈、乳癖、乳汁少等乳部疾患；②咳嗽，气喘，呃逆；③胸痛。', '隔物灸仪艾灸时间：30-40分钟；温度：38-45℃；<br>艾条悬灸时间：10-20分钟；<br>艾炷灸时间：5-7壮。', '现代常用于治疗乳腺炎、乳汁缺乏、肋间神经痛、支气管炎等。配少泽、足三里主治产后乳汁不足。', '膺窗乳中乳根_opt', '41', '①乳痈、乳癖、乳汁少等乳部疾患；②咳嗽，气喘，呃逆；③胸痛。', '咳嗽,气喘,呃逆,胸痛', '现代常用于治疗乳腺炎、乳汁缺乏、肋间神经痛、支气管炎等。配少泽、足三里主治产后乳汁不足。', '乳腺炎,支气管炎', '隔物灸仪艾灸时间：30-40分钟；温度：38-45℃；\r\n艾条悬灸时间：10-20分钟；\r\n艾炷灸时间：5-7壮。', '自我按摩，用食中指的指腹端轻轻揉按此穴。', '', '', '向外斜刺0.5-0.8寸，局部胀痛。《玉龙歌》“哮喘之症嗽痰多，若用金针疾自和，俞府乳根一样刺，气喘风痰渐渐磨”。其穴有理气活血，通经下乳之功，是治疗乳部疾患和心前区疼痛的主穴之一。《金鉴》用此穴治疗小儿龟胸。', '', '');
INSERT INTO `xwDetail` VALUES ('42', '二白', '15', 'èrbái', 'eb', 'EX-UE2', '', '', '腕横纹上4寸，桡侧腕屈肌腱的两侧，一侧各1穴；一臂2穴，左右两臂共4穴。', '第1步：伸臂立掌，用力握拳；<br>第2步：在前臂拇指侧可见明显突起的一条索筋（桡侧腕屈肌腱）；<br>第3步：将近掌侧腕横纹到肘横纹的距离平分为3等份；<br>第4步：在近掌侧1/3交点处，该索筋的两侧，用力按压有酸胀感处，即为本穴。', '①痔疮，脱肛；②前臂痛；胸胁痛。', '隔物灸仪艾灸时间：30-50分钟；温度：38-48 ℃；<br>艾条悬灸时间：5-10分钟；<br>艾炷灸时间：3-5壮。', '①现代常用于治疗痔疮、脱肛等肛肠病。<br>②《马氏温灸法》：痈疽。', '二白_opt', '42', '①痔疮，脱肛；②前臂痛；胸胁痛。', '痔疮,脱肛', '①现代常用于治疗痔疮、脱肛等肛肠病。\r\n②《马氏温灸法》：痈疽。', '', '隔物灸仪艾灸时间：30-50分钟；温度：38-48 ℃；\r\n艾条悬灸时间：5-10分钟；\r\n艾炷灸时间：3-5壮。', '', '', '', '', '', '');
INSERT INTO `xwDetail` VALUES ('43', '二间', '2', 'ěrjiān', 'ej', 'LI2', '荥穴', '', '微握拳，当食指桡侧第2掌指关节前凹陷中。', '伸臂，微握拳。手食指第2掌指关节前缘桡侧皮肤皱褶顶点，触之有凹陷处，即为本穴。', '①齿痛、鼻衄等五官疾患；②热病；③肩周炎。', '艾条悬灸：5-10分钟；米粒灸：3-5壮。', '现代常用于治疗牙痛、咽喉炎、扁桃体炎等。配鱼际、合谷主治咽喉肿痛、牙痛。', '商阳二间三间_opt', '43', '①齿痛、鼻衄等五官疾患；②热病；③肩周炎。', '牙痛,肩周炎', '现代常用于治疗牙痛、咽喉炎、扁桃体炎等。配鱼际、合谷主治咽喉肿痛、牙痛。', '咽喉炎,扁桃体炎', '艾条悬灸：5-10分钟；米粒灸：3-5壮。', '自我按摩，用食指近端指间关节点按二间穴5-10次。', '', '', '直刺0.2-0.3寸。局部痛麻。《百症赋》“寒栗恶寒，二间疏通阴郄暗”《通玄指要赋》“目昏不见，二间宜取”。二间和三间部位相近，但二间长于治疗鼻出血，三间长于治疗本经的肠鸣音亢进、急性腹泻。', '', '');
INSERT INTO `xwDetail` VALUES ('44', '云门', '1', 'Yúnmén', 'ym', 'LU2', '', '', '在胸前壁的外上方，肩胛骨喙突上方，锁骨下窝凹陷处，距前正中线6寸。', '第1步：正立或正坐位，双手叉腰；<br>第2步：在锁骨外侧下缘可见一三角形凹陷处，即为本穴。', '①咳嗽、气喘；②胸痛，肩背痛。', '隔物灸仪艾灸时间：30-50分钟；温度：38-45℃；<br>艾条悬灸时间：10-15分钟；<br>艾炷灸时间：3-5壮。', '现代常用于治疗气管炎、支气管哮喘、肋间神经痛、局部软组织疾患等。配肺俞主治咳嗽、气喘；配中府、期门等主治胸肋痛。', '002云门中府_opt', '44', '①咳嗽、气喘；②胸痛，肩背痛。', '咳嗽,气喘,胸痛', '现代常用于治疗气管炎、支气管哮喘、肋间神经痛、局部软组织疾患等。配肺俞主治咳嗽、气喘；配中府、期门等主治胸肋痛。', '支气管哮喘', '隔物灸仪艾灸时间：30-50分钟；温度：38-45℃；\r\n艾条悬灸时间：10-15分钟；\r\n艾炷灸时间：3-5壮。', '自我按摩，食指放在中指上，用中指指腹点按揉运穴位的酸胀处3分钟。', '', '', '向外斜刺0．3～0．5寸，可沿本经传到至上肢远端。《甲乙经》“刺太深，令人逆息”。临床上要牢牢掌握针刺的深度和角度，不可向内侧深刺，以防刺伤肺脏。\r\n《甲乙经》“肩痛不可举，引缺盆痛，云门主之”云门穴下为喙突肩喙突锁骨韧带包括斜方韧带和圆锥韧带，针刺云门穴可以松解肩部喙锁韧带，增加肩部上举活动度，临床中对肩周炎有效。', '', '');
INSERT INTO `xwDetail` VALUES ('45', '五处', '7', 'Wǔchù', 'wc', 'BL5', '', '', '在头部，当前发际正中直上1寸，旁开1.5寸。', '正坐或仰卧位。从前发际正中直上1横指，再旁开量2横指（食、中指），适对鼻侧直上，按压有痛感处，即为本穴。', '①头痛、目眩、视力减退；②癫痫。', '隔物灸仪艾灸时间：20-30分钟；温度：38-50℃；<br>艾条悬灸时间：5-10分钟。', '现代常用于治疗癫痫、三叉神经痛、结膜炎等。配身柱、委中、委阳、昆仑主治脊强反折、癫狂；配下廉、神庭主治头风。', '眉冲曲差五处_opt', '45', '①头痛、目眩、视力减退；②癫痫。', '头痛', '现代常用于治疗癫痫、三叉神经痛、结膜炎等。配身柱、委中、委阳、昆仑主治脊强反折、癫狂；配下廉、神庭主治头风。', '三叉神经痛,急性结膜炎', '隔物灸仪艾灸时间：20-30分钟；温度：38-50℃；\r\n艾条悬灸时间：5-10分钟。', '自我按摩，用食指的指腹端轻轻揉按此穴。', '', '', '平刺0．5～0．8寸。', '', '');
INSERT INTO `xwDetail` VALUES ('46', '五枢', '11', 'wǔshū', 'ws', 'GB27', '足少阳、带脉交会穴', '', '在侧腹部，当髂前上棘的前方，横平脐下3寸处。', '第1步：仰卧位；<br>第2步：确定耻骨联合：沿下腹部前正中线垂直向下推，可触及一骨头，此骨头即为耻骨联合；<br>第3步：将脐中与耻骨联合上缘中点的连线平分为5等分；<br>第4步：从该连线的上3/5与下2/5交点处作一水平线；<br>第5步：在骨盆的前上方可摸到一骨突起（即髂前上棘），该骨突起的前方和此线相交处，即为本穴。', '①带下、月经不调、阴挺、小腹痛等妇科病证；②疝痛，睾丸炎；③腰痛，便秘。', '隔物灸仪艾灸时间：30-50分钟；温度：38-50℃；<br>艾条悬灸时间：10-20分钟；<br>艾炷灸时间：3-5壮。', '现代常用于治疗附件炎、子宫内膜炎、子宫脱垂等。配太冲、曲泉主治少腹痛、疝气。', '150五枢维道_opt', '46', '①带下、月经不调、阴挺、小腹痛等妇科病证；②疝痛，睾丸炎；③腰痛，便秘。', '带下病,月经不调,腹痛,睾丸炎,腰痛,便秘', '现代常用于治疗附件炎、子宫内膜炎、子宫脱垂等。配太冲、曲泉主治少腹痛、疝气。', '子宫脱垂', '隔物灸仪艾灸时间：30-50分钟；温度：38-50℃；\r\n艾条悬灸时间：10-20分钟；\r\n艾炷灸时间：3-5壮。', '自我按摩，用手食指或中指的指腹端揉按此穴。', '', '', '直刺1～2寸。《玉龙经》“五枢亦治腰间痛，得穴方知病顿轻”。', '', '');
INSERT INTO `xwDetail` VALUES ('47', '交信', '8', 'Jiāoxìn', 'jx', 'KI8', '阴跷脉之郄穴', '', '在小腿内侧，当太溪直上2寸，复溜前0.5寸，胫骨内侧缘的后方。', '第1步：坐位垂足或仰卧位；<br>第2步：确定太溪穴：由足内踝尖向后推至与跟腱之间的凹陷处（大约当内踝尖与跟腱之间中点），即为太溪穴；<br>第3步:确定复溜穴：从太溪穴直上量2横指（大拇指指间关节部位的横径为1寸），跟腱的前方，即为复溜穴；<br>第4步：在复溜穴前半横指处，即为本穴。', '①月经不调、痛经、崩漏等妇科病症；②腹痛、腹泻；③小便不利、水肿；④睾丸肿痛、疝气；膝、股、腘内侧痛。', '隔物灸仪艾灸时间：30-50分钟；温度：38-45℃；<br>艾条悬灸时间：5-10分钟；<br>艾炷灸时间：3-5壮。', '现代常用于治疗月经不调、肾炎、睾丸炎、尿路感染等。配三阴交主治月经不调。', '109复溜交信筑宾_opt', '47', '①月经不调、痛经、崩漏等妇科病症；②腹痛、腹泻；③小便不利、水肿；④睾丸肿痛、疝气；膝、股、腘内侧痛。', '痛经,功能性子宫出血（崩漏）,腹痛,腹泻,小便不利', '现代常用于治疗月经不调、肾炎、睾丸炎、尿路感染等。配三阴交主治月经不调。', '月经不调,肾炎,睾丸炎', '隔物灸仪艾灸时间：30-50分钟；温度：38-45℃；\r\n艾条悬灸时间：5-10分钟；\r\n艾炷灸时间：3-5壮。', '自我按摩，用手拇指或食指的指腹端轻轻揉按此穴。', '', '', '直刺0.8-1.2寸，局部麻胀，《肘后歌》“腰膝强痛交信凭”《百症赋》“女子少气漏血，不无交信合阳”。', '', '');
INSERT INTO `xwDetail` VALUES ('48', '京门', '11', 'Jīngmén', 'jm', 'GB25', '肾之募穴', '', '在侧腰部，章门后1.8寸，当第12肋骨游离端的下方。', '第1步：侧坐或侧卧位；<br>第2步：沿胁向腰部触摸，至第12肋游离端，其下方即为本穴。', '①小便不利，水肿；②腰胁痛；③肠鸣，泄泻，腹胀，呕吐。', '隔物灸仪艾灸时间：30-50分钟；温度：38-45℃；<br>艾条悬灸时间：10-20分钟；<br>艾炷灸时间：5-9壮。', '现代常用于治疗肾炎、高血压、带状疱疹等。配肾俞、三阴交主治肾虚腰痛；配天枢、中脘、支沟主治腹胀。', '京门带脉_opt', '48', '①小便不利，水肿；②腰胁痛；③肠鸣，泄泻，腹胀，呕吐。', '小便不利,肠鸣,腹胀,呕吐', '现代常用于治疗肾炎、高血压、带状疱疹等。配肾俞、三阴交主治肾虚腰痛；配天枢、中脘、支沟主治腹胀。', '肾炎,带状疱疹及后遗神经痛', '隔物灸仪艾灸时间：30-50分钟；温度：38-45℃；\r\n艾条悬灸时间：10-20分钟；\r\n艾炷灸时间：5-9壮。', '自我按摩，用手食指或中指的指腹端轻轻揉按此穴。', '', '', '斜刺0.5～0.8寸，《甲乙》卷九“腰痛不可以久立俯仰，京门及行间主之”。', '', '');
INSERT INTO `xwDetail` VALUES ('49', '京骨', '7', 'Jīnggǔ', 'jg', 'BL64', '原穴', '', '在足外侧，第5跖骨粗隆下方，赤白肉际处。', '正坐垂足着地或俯卧位。沿着小趾后面的长骨往后推，可触摸到一凸起（即第5跖骨粗隆），其凸起下方掌背交界线（即赤白肉际处），按压可及一凹陷处，即为本穴。', '①头痛，项强；②腰痛；③癫痫。', '隔物灸仪艾灸时间：30-40分钟；温度：38-45℃；<br>艾条悬灸时间：5-10分钟；<br>艾炷灸时间：3-5壮。', '现代常用于治疗高血压、落枕、腰肌劳损等。配风池、天柱主治头痛项强。', '金门京骨_opt', '49', '①头痛，项强；②腰痛；③癫痫。', '头痛,项强,腰痛,癫痫', '现代常用于治疗高血压、落枕、腰肌劳损等。配风池、天柱主治头痛项强。', '落枕,腰肌劳损', '隔物灸仪艾灸时间：30-40分钟；温度：38-45℃；\r\n艾条悬灸时间：5-10分钟；\r\n艾炷灸时间：3-5壮。', '用拇指的指腹端揉按此穴。', '', '', '直刺0．3～0．5寸。《大成》卷六“主头痛如破，腰痛不可屈伸，身后痛，侧身痛，目内眦赤烂，白翳侠内眦起......”。', '', '');
INSERT INTO `xwDetail` VALUES ('50', '人迎', '3', 'Rényíng', 'ry', 'ST9', '足阳明、少阳交会穴', '', '在颈部，喉结旁，当胸锁乳突肌的前缘，颈总动脉搏动处。', '第1步：正坐位，头微侧；<br>第2步：在颈侧部，可见触及一条明显的肌肉，即为胸锁乳突肌；<br>第3步：在平喉结的胸锁乳突肌前缘，颈部动脉搏动处，即为本穴。', '①瘿气、咽喉肿痛、瘰疬；②高血压；③气喘、肺结核、声带疾病；④头痛，心脏神经官能症。', '', '现代常用于治疗颈淋巴结核、甲状腺肿大、支气管哮喘、高血压等。配天突、中封等主治单纯性甲状腺肿；配曲池、太冲主治高血压。<br>《针灸资生经》云：禁灸，灸之不幸伤人。', '人迎水突_opt', '50', '①瘿气、咽喉肿痛、瘰疬；②高血压；③气喘、肺结核、声带疾病；④头痛，心脏神经官能症。', '气喘,肺结核,头痛', '现代常用于治疗颈淋巴结核、甲状腺肿大、支气管哮喘、高血压等。配天突、中封等主治单纯性甲状腺肿；配曲池、太冲主治高血压。\r\n', '颈淋巴结核,支气管哮喘', '《针灸资生经》云：禁灸，灸之不幸伤人。', '', '', '', '在喉结旁，胸锁乳突肌前缘，颈总动脉内侧处取之。避开颈总动脉直刺0.3-0.8寸，局部麻胀，有时传至手，《甲乙经》“禁不可灸，刺入四分，过深不幸杀人”，临床上此穴针刺不宜超过0.8寸，以免造成医疗事故。《金针梅花诗钞》“人迎喉旁动应手，位高法天测生死，喉痹咳喘虽有功，重在指迷津慎着针灸”，古人在三部九侯诊法中，人迎法天，寸口法地，跗阳法地，三者同时并重。', 'http://video.jiudafu.com/videocolumn/renyin.mp4', 'http://user.jiudafu.com/xw/cover/1520846062-acupuncture-50.jpg');
INSERT INTO `xwDetail` VALUES ('51', '仆参', '7', 'Púcān', 'pc', 'BL61', '', '', '在足外侧部，外踝后下方，昆仑穴直下，跟骨外侧，赤白肉际处。', '正坐垂足着地，或俯卧位。先确定昆仑（参见“昆仑”），再从昆仑垂直向下量1横指，按压有酸胀感处，即为本穴。', '①下肢痿痹，足跟痛；②癫痫，尿道炎，鼻出血。', '隔物灸仪艾灸时间：30-40分钟；温度：38-48℃；<br>艾条悬灸时间：5-10分钟；<br>艾炷灸时间：3-5壮。', '现代常用于治疗踝关节炎、下肢瘫痪、尿路感染、癫痫等。配水沟、十宣主治癫痫；配昆仑、太溪主治足跟痛。', '102昆仑仆参申脉_opt', '51', '①下肢痿痹，足跟痛；②癫痫，尿道炎，鼻出血。', '癫痫,尿道炎,鼻出血', '现代常用于治疗踝关节炎、下肢瘫痪、尿路感染、癫痫等。配水沟、十宣主治癫痫；配昆仑、太溪主治足跟痛。', '', '隔物灸仪艾灸时间：30-40分钟；温度：38-48℃；\r\n艾条悬灸时间：5-10分钟；\r\n艾炷灸时间：3-5壮。', '用拇指的指腹端揉按此穴。', '', '', '直刺0．3～0．5寸。 《甲乙》卷九“腰痛不可举，足跟中踝后痛，仆参主之”。', '', '');
INSERT INTO `xwDetail` VALUES ('52', '伏兔', '3', 'Fútù', 'ft', 'ST32', '', '', '在大腿前面，当髂前上棘与髌底外侧端的连线上，髌底上6寸。', '第1步：仰卧或坐位，屈膝成90度；<br>第2步：用左手取右腿穴，右手取左腿穴；<br>第3步：以手掌后第1横纹中点按在膝盖骨（髌骨）上缘中点，手指并拢压在大腿上，当中指尖端所达处，即为本穴。', '①下肢痿痹、腰痛、膝冷等腰及下肢病证；②疝气；③脚气。', '隔物灸仪艾灸时间：30-50分钟；温度：38-48℃；<br>艾条悬灸时间：10-20分钟；<br>艾炷灸时间：5-7壮。', '现代常用于治疗下肢瘫痪、股外侧皮神经炎及膝关节病变等。配髀关、犊鼻主治腿膝疼痛。', '髀关伏兔_opt', '52', '①下肢痿痹、腰痛、膝冷等腰及下肢病证；②疝气；③脚气。', '腰痛', '现代常用于治疗下肢瘫痪、股外侧皮神经炎及膝关节病变等。配髀关、犊鼻主治腿膝疼痛。', '股外侧皮神经炎', '隔物灸仪艾灸时间：30-50分钟；温度：38-48℃；\r\n艾条悬灸时间：10-20分钟；\r\n艾炷灸时间：5-7壮。', '自我按摩，用拇指的指腹端轻轻揉按此穴。', '', '', '直刺1～2寸。局部麻胀。《大成》卷六“主膝冷不得温”，针刺伏兔穴可以治疗血尿、毛细血管出血。', '', '');
INSERT INTO `xwDetail` VALUES ('53', '会宗', '10', 'Huìzōng', 'hz', 'SJ7', '郄穴', '', '在前臂背侧，当腕背横纹上3寸，支沟尺侧，尺骨的桡侧缘。', '抬臂俯掌。从腕背横纹向上量4横指，在尺骨的桡侧（拇指侧）缘，按压有酸胀感处，即为本穴。', '①耳聋，耳鸣；②上肢肌肤痛；③癫痫。', '隔物灸仪艾灸时间：20-30分钟；温度：38-48℃；<br>艾条悬灸时间：5-10分钟；<br>艾炷灸时间：3-5壮。', '现代常用于治疗神经性耳聋、前臂疼痛麻木等。配听会、翳风主治耳鸣、耳聋；配臂臑、曲池主治上肢痹痛；配大椎、百会主治小儿癫痫。', '126外关支沟会宗_opt', '53', '①耳聋，耳鸣；②上肢肌肤痛；③癫痫。', '耳鸣、耳聋,癫痫', '现代常用于治疗神经性耳聋、前臂疼痛麻木等。配听会、翳风主治耳鸣、耳聋；配臂臑、曲池主治上肢痹痛；配大椎、百会主治小儿癫痫。', '', '隔物灸仪艾灸时间：20-30分钟；温度：38-48℃；\r\n艾条悬灸时间：5-10分钟；\r\n艾炷灸时间：3-5壮。', '自我按摩，用手拇指或食指的指腹端轻轻揉按此穴。', '', '', '直刺0.5～1寸。《会宗》“主肌肉痛，耳聋，羊痫”。', '', '');
INSERT INTO `xwDetail` VALUES ('54', '会阳', '7', 'Huìyáng', 'hy', 'BL35', '', '', '在骶部，尾骨端旁开0.5寸。', '俯卧或跪伏位。下髎下，尾骨下端旁软陷处，即为本穴。', '①痔疾、腹泻；②阳痿；③带下。', '隔物灸仪艾灸时间：30-40分钟；温度：38-48℃；<br>艾条悬灸时间：10-15分钟；<br>艾炷灸时间：5-7壮。', '现代常用于治疗前列腺炎、肠炎、痔疮等。配百会、长强主治脱肛、痔疮。', '中髎下髎会阳_opt', '54', '①痔疾、腹泻；②阳痿；③带下。', '腹泻,阳痿,带下病', '现代常用于治疗前列腺炎、肠炎、痔疮等。配百会、长强主治脱肛、痔疮。', '前列腺炎,急性胃肠炎,痔疮,脱肛', '隔物灸仪艾灸时间：30-40分钟；温度：38-48℃；\r\n艾条悬灸时间：10-15分钟；\r\n艾炷灸时间：5-7壮。', '自我按摩，用拇指的指腹端揉按此穴。', '', '', '直刺1-1.5寸。', '', '');
INSERT INTO `xwDetail` VALUES ('55', '会阴', '14', 'Huìyīn', 'hy', 'RN1', '任脉、督脉、冲脉交会穴', '', '在会阴部，男性当阴囊根部与肛门连线的中点，女性当大阴唇后联合中与肛门连线的中点。', '仰卧屈膝。在会阴部，取两阴连线的中点，即为本穴。', '①小便不利、遗尿、遗精、阳痿、阴痒、阴痛等前阴病证；②月经不调等妇科病证；③溺水窒息，昏迷，癫狂。', '艾条悬灸：5-10分钟。', '现代常用于治疗阴道炎、睾丸炎、前列腺炎等。配中极、肩井主治难产；配人中、阴陵泉主治溺水窒息。', '会阴_opt', '55', '①小便不利、遗尿、遗精、阳痿、阴痒、阴痛等前阴病证；②月经不调等妇科病证；③溺水窒息，昏迷，癫狂。', '小便不利,遗尿,遗精,阳痿,阴痛,月经不调', '现代常用于治疗阴道炎、睾丸炎、前列腺炎等。配中极、肩井主治难产；配人中、阴陵泉主治溺水窒息。', '睾丸炎,前列腺炎', '艾条悬灸：5-10分钟。', '', '', '', '直刺0.5～1寸；孕妇慎用。', '', '');
INSERT INTO `xwDetail` VALUES ('56', '侠溪', '11', 'Xiáxī', 'xx', 'GB43', '荥穴', '', '在足背外侧，当第4、5趾间，趾蹼缘后方赤白肉际处。', '坐位或仰卧位。在足背第4、5两趾之间连接处的缝纹头，按压有酸胀感处，即为本穴。', '①头痛，目痛，耳鸣，耳聋，眩晕等头面五官病证；②胸胁胀痛；③足跗肿痛；④热病；⑤腋淋巴结炎，咳血，乳腺炎。', '艾条悬灸：5-10分钟；<br>艾炷灸时间：3-5壮；麦粒灸：3-5壮。', '现代常用于治疗神经性头痛、结膜炎、神经性耳鸣、中耳炎、高血压、带状疱疹等。配支沟、阳陵泉、主治胸胁胀痛；配听宫、翳风主治耳鸣、耳聋。', '侠溪足窍阴_opt', '56', '①头痛，目痛，耳鸣，耳聋，眩晕等头面五官病证；②胸胁胀痛；③足跗肿痛；④热病；⑤腋淋巴结炎，咳血，乳腺炎。', '头痛,耳鸣、耳聋,眩晕,乳腺炎', '现代常用于治疗神经性头痛、结膜炎、神经性耳鸣、中耳炎、高血压、带状疱疹等。配支沟、阳陵泉、主治胸胁胀痛；配听宫、翳风主治耳鸣、耳聋。', '急性结膜炎,化脓性中耳炎,带状疱疹及后遗神经痛', '艾条悬灸：5-10分钟；\r\n艾炷灸时间：3-5壮；麦粒灸：3-5壮。', '自我按摩，用手食指或中指的指腹端揉按此穴。', '', '', '直刺0.3～0.5寸。《千金》卷三十“侠溪主外眦赤痛，逆寒泣出，目痒。”《百症赋》“兼阳谷，治颌肿口噤”。', '', '');
INSERT INTO `xwDetail` VALUES ('57', '侠白', '1', 'Xiábái', 'xb', 'LU4', '', '', '在臂内侧面，肱二头肌桡侧缘，腋前纹头下4寸，或肘横纹上5寸处。', '两手合掌向前伸直，夹住乳房，此时乳头所对的手臂内侧处即为本穴。', '①咳嗽、气喘；②干呕；③上臂痛；④心动过速。', '隔物灸仪艾灸时间：30-40分钟；温度：38-45℃；<br>艾条悬灸时间：5-10分钟。', '现代常用于治疗支气管哮喘、支气管炎等。配间使、内关等主治正中神经痛。', '003天府侠白尺泽_opt', '57', '①咳嗽、气喘；②干呕；③上臂痛；④心动过速。', '咳嗽,气喘,干呕', '现代常用于治疗支气管哮喘、支气管炎等。配间使、内关等主治正中神经痛。', '支气管哮喘,支气管炎', '隔物灸仪艾灸时间：30-40分钟；温度：38-45℃；\r\n艾条悬灸时间：5-10分钟。', '自我按摩，食指放在中指上，用中指指腹点按揉运穴位的酸胀处3分钟。', '', '', '直刺0．5～1寸，局部酸胀，或似触电样传至胸前。《甲乙经》卷九“心痛，侠白主之”。', '', '');
INSERT INTO `xwDetail` VALUES ('58', '俞府', '8', 'Shūfǔ', 'sf', 'KI27', '', '', '在胸部，当锁骨下缘，前正中线旁开2寸。', '第1步：仰卧位；<br>第2步：从乳中线（过乳头的垂直线）与锁骨下缘的交点处作一水平线；<br>第3步：该线与前正中线有一交点，两点之间的中点处，即为本穴。', '①咳嗽，气喘，胸痛；②呕吐，不嗜食。', '隔物灸仪艾灸时间：30-50分钟；温度：38-50℃；<br>艾条悬灸时间：10-15分钟；<br>艾炷灸时间：3-5壮。', '现代常用于治疗支气管炎、支气管哮喘、肋间神经痛等。配肺俞、膻中、丰隆主治咳喘多痰；配天突主治呕吐。', '116神藏彧中俞府_opt', '58', '①咳嗽，气喘，胸痛；②呕吐，不嗜食。', '咳嗽,气喘,呕吐', '现代常用于治疗支气管炎、支气管哮喘、肋间神经痛等。配肺俞、膻中、丰隆主治咳喘多痰；配天突主治呕吐。', '支气管炎,支气管哮喘', '隔物灸仪艾灸时间：30-50分钟；温度：38-50℃；\r\n艾条悬灸时间：10-15分钟；\r\n艾炷灸时间：3-5壮。', '自我按摩，用手拇指或食指的指腹端轻轻揉按此穴。', '', '', ' 斜刺或平刺0.5～0.8寸，不可深刺，以免伤及心、肺。', '', '');
INSERT INTO `xwDetail` VALUES ('59', '偏历', '2', 'Piānlì', 'pl', 'LI6', '络穴', '', '屈肘，在前臂背面桡侧，当阳溪穴与曲池穴连线上，腕横纹上3寸。', '第1步：两虎口垂直交；<br>第2步：当中指端落于前臂背面，指端下有一凹陷，即为本穴。', '①耳鸣、鼻衄、结膜炎等五官疾患；②手臂酸痛；③腹部胀满，水肿。', '隔物灸仪艾灸时间：30-40分钟；温度：38-45℃；<br>艾条悬灸时间：5-10分钟；<br>艾炷灸时间：3-5壮。', '现代常用于治疗扁桃体炎、水肿、前臂神经痛等。配阴陵泉、水分主治水肿；配阳溪、商阳等主治实邪耳鸣。', '011偏历温溜下廉_opt', '59', '①耳鸣、鼻衄、结膜炎等五官疾患；②手臂酸痛；③腹部胀满，水肿。', '耳鸣、耳聋,急性结膜炎', '现代常用于治疗扁桃体炎、水肿、前臂神经痛等。配阴陵泉、水分主治水肿；配阳溪、商阳等主治实邪耳鸣。', '扁桃体炎', '隔物灸仪艾灸时间：30-40分钟；温度：38-45℃；\r\n艾条悬灸时间：5-10分钟；\r\n艾炷灸时间：3-5壮。', '自我按摩，用拇指远端指腹点按弹拨该穴位。', '', '', '直刺或斜刺，针尖向肘微斜刺入0．5～0．8寸，局部麻胀。《灵枢.经脉》“手阳明之别，名曰偏历。实则龋聋，虚则齿寒痹隔（齿冷，胸膈闭阻不通）。取之所别也”。肺为水之上源，大肠则“是主液所生病者”。取二经之络穴而治之，故能利小便而消鼓胀，祛风寒而通腠理。', '', '');
INSERT INTO `xwDetail` VALUES ('60', '光明', '11', 'Guāngmíng', 'gm', 'GB37', '络穴', '', '在小腿外侧，当外踝尖上5寸，腓骨前缘。', '第1步：坐位或仰卧位；<br>第2步：取外踝尖到腘横纹头的中点（外踝尖到过横纹头的距离为16寸）；<br>第3步：从此中点往下量4横指（示指、中指、无名指、小指四指并拢，以中指近端指间关节横纹水平的四指宽度为3寸，也称一夫法），在小腿外侧骨头（腓骨）前缘处，即为本穴。', '①目痛、夜盲、目视不明等目疾；②下肢痿痹；③乳房胀痛，乳少。', '隔物灸仪艾灸时间：30-70分钟；温度：38-50℃；<br>艾条悬灸时间：5-10分钟；<br>艾炷灸时间：3-5壮。', '现代常用于治疗视神经萎缩、白内障、近视、乳腺炎、产后缺乳等。配睛明、承泣、瞳子髎主治目痛；配太冲对青少年近视有效；配足临泣可回乳。', '光明阳辅_opt', '60', '①目痛、夜盲、目视不明等目疾；②下肢痿痹；③乳房胀痛，乳少。', '夜盲', '现代常用于治疗视神经萎缩、白内障、近视、乳腺炎、产后缺乳等。配睛明、承泣、瞳子髎主治目痛；配太冲对青少年近视有效；配足临泣可回乳。', '近视,乳腺炎,产后缺乳', '隔物灸仪艾灸时间：30-70分钟；温度：38-50℃；\r\n艾条悬灸时间：5-10分钟；\r\n艾炷灸时间：3-5壮。', '自我按摩，用手食指或中指的指腹端揉按此穴。', '', '', '直刺1-1.5寸。《标幽赋》“眼痒眼痛，泻光明与地会”。', '', '');
INSERT INTO `xwDetail` VALUES ('61', '兑端', '13', 'Duìduān', 'dd', 'DU27', '', '', '在面部，当上唇的尖端，人中沟下端的皮肤与唇的移行部。', '仰靠或仰卧位。上唇正中的尖端，红唇与皮肤移行处。', '①癫狂，晕厥；②牙龈肿痛，口歪，鼻衄；③鼻塞。', '不宜灸。', '现代常用于治疗癔病等。配内关主治晕厥；配本神、丰隆主治癫疾。', '素髎水沟兑端_opt', '61', '①癫狂，晕厥；②牙龈肿痛，口歪，鼻衄；③鼻塞。', '晕厥,鼻塞', '现代常用于治疗癔病等。配内关主治晕厥；配本神、丰隆主治癫疾。', '', '不宜灸。', '', '', '', '', '', '');
INSERT INTO `xwDetail` VALUES ('62', '八邪', '15', 'Bāxié', 'bx', 'EX-UE9', '', '', '微握拳，在手背侧，第1至第5指间，指蹼缘后方赤白肉际处，左右共8穴。', '伸臂俯掌。手背掌指关节前，第1至第5指间的缝纹端后方掌背交界线（即赤白肉际处），即为本穴，左右共8个。', '①毒蛇咬伤，手背红肿，手指麻木；②目痛，烦热。', '艾条悬灸：5-10分钟。', '配后溪、三间主治手指麻痛。', '中魁八邪大骨空小骨空_opt', '62', '①毒蛇咬伤，手背红肿，手指麻木；②目痛，烦热。', '五心烦热', '配后溪、三间主治手指麻痛。', '', '艾条悬灸：5-10分钟。', '', '', '', '斜刺0.5~0.8寸，或者点刺出血。', '', '');
INSERT INTO `xwDetail` VALUES ('63', '八风', '15', 'Bāfēng', 'bf', 'EX-LE10', '', '', '正坐或仰卧位。在足背侧，第1至第5趾间，趾蹼缘后方赤白肉际处，一侧4穴，左右共8个穴位。', '正坐位或仰卧位。足5趾各趾间缝纹头尽处，即为本穴。', '毒蛇咬伤，足跗肿痛，肢弱无力，脚气，牙痛，胃痛，月经不调。', '艾条悬灸：5-10分钟。', '', '八风气端_opt', '63', '毒蛇咬伤，足跗肿痛，肢弱无力，脚气，牙痛，胃痛，月经不调。', '牙痛,月经不调', '', '', '艾条悬灸：5-10分钟。', '', '', '', '斜刺0.5~0.8寸，或点刺出血。', '', '');
INSERT INTO `xwDetail` VALUES ('64', '八髎', '15', 'Bāliáo', 'bl', '', '', '', '位置：骶椎。又称上髎、次髎、中髎和下髎，左右共八个穴位。', '第1步：俯卧位；<br>第2步：确定髂后上棘：从骨盆最高点向内下方骶角两侧循摸一高骨突起，即是髂后上棘；<br>第3步：确定上髎穴：髂后上棘与骶后正中线之间的凹陷处，即为上髎穴。<br>第4步：确定骶管裂孔：在尾骨上方，两骶角之间即为骶管裂孔；<br>第5步：把食指按在上髎穴处，小指按在骶管裂孔旁，食、中、环、小指等距离分开，各指尖端所指处即是上、次、中、下髎，左右共八个穴位。', '腰骶部疾病、下腰痛、坐骨神经痛、下肢痿痹、小便不利、月经不调、小腹胀痛、盆腔炎等病症。', '隔物灸仪艾灸时间：30-70分钟；温度：38-48 ℃；<br>艾条悬灸时间：10-15分钟。', '', '上髎次髎中髎下髎八髎_opt', '', '', '', '', '', '', '', '', '', '', '', '');
INSERT INTO `xwDetail` VALUES ('65', '公孙', '4', 'Gōngsūn', 'gs', 'SP4', '络穴，八脉交会穴，通冲脉', '', '在足内侧缘，当第1跖骨基底部的前下方。', '第1步：正坐垂足或仰卧；<br>第2步：由足大趾与足掌所构成的关节（第1跖趾关节）内侧，往后用手推有一弓形骨（足弓）；<br>第3步：在弓形骨前端下缘，可触及一凹陷，按压有酸胀感，即为本穴。', '①胃痛、呕吐、腹痛、腹泻、痢疾等胃肠病证；②心烦、失眠、狂证等神志病证；③逆气里急、气上冲心（奔豚气）等冲脉病证。', '隔物灸仪艾灸时间：30-50分钟；温度：38-48℃；<br>艾条悬灸时间：5-10分钟；<br>艾炷灸时间：3-5壮。', '现代常用于治疗急慢性胃炎、消化道溃疡、急慢性肠炎、神经性呕吐、消化不良、精神分裂症等。配中脘、足三里主治胃脘胀痛；配丰隆、膻中主治呕吐、眩晕。', '公孙商丘_opt', '65', '①胃痛、呕吐、腹痛、腹泻、痢疾等胃肠病证；②心烦、失眠、狂证等神志病证；③逆气里急、气上冲心（奔豚气）等冲脉病证。', '呕吐,腹痛,腹泻,痢疾,心烦,失眠', '现代常用于治疗急慢性胃炎、消化道溃疡、急慢性肠炎、神经性呕吐、消化不良、精神分裂症等。配中脘、足三里主治胃脘胀痛；配丰隆、膻中主治呕吐、眩晕。', '慢性胃炎,急性胃肠炎,呕吐,眩晕', '隔物灸仪艾灸时间：30-50分钟；温度：38-48℃；\r\n艾条悬灸时间：5-10分钟；\r\n艾炷灸时间：3-5壮。', '自我按摩，用拇指的指腹端轻轻揉按此穴。', '', '', '直刺0.5-1.2寸。', '', '');
INSERT INTO `xwDetail` VALUES ('66', '关元', '14', 'Guānyuán', 'gy', 'RN4', '小肠募穴，任脉、足三阴经交会穴', '', '在下腹部，前正中线上，当脐中下3寸。', '第1步：仰卧位或正坐位；<br>第2步：确定耻骨联合：沿下腹部前正中线垂直向下推，可触及一骨头，此骨头即为耻骨联合；<br>第3步：将脐中与耻骨联合上缘中点的连线平分为5等分；<br>第4步：该连线的上3/5与下2/5交点处即为本穴。', '①遗精、阳痿、早泄、尿闭、尿频等泌尿生殖系病证；②月经不调、带下、痛经等妇科病证；③中风脱证、虚劳冷惫、羸瘦无力等元气虚损病证；④腹痛、泄泻、痢疾、脱肛等肠腑病证。', '隔物灸仪艾灸时间：30-90分钟；温度：38-52℃；<br>艾条悬灸时间：10-20分钟；<br>艾炷灸时间：5-9壮。', '①现代常用于治疗男子性功能障碍、尿潴留、肾炎、膀胱炎、前列腺炎、功能性子宫出血、阴道炎、遗尿、低血压、神经衰弱、肠炎等。配中极、百会、三阴交主治遗尿；配肾俞、气海主治肾虚尿频；配肾俞、足三里、三阴交、然谷主治男子不育症；配肾俞、太溪主治久痢。<br>②《名堂》云：若怀胎必不针，若针而胎落，胎多不出。岐伯云：但是积冷虚乏皆宜灸。', '曲骨中极关元石门_opt', '66', '①遗精、阳痿、早泄、尿闭、尿频等泌尿生殖系病证；②月经不调、带下、痛经等妇科病证；③中风脱证、虚劳冷惫、羸瘦无力等元气虚损病证；④腹痛、泄泻、痢疾、脱肛等肠腑病证。', '遗精,阳痿,早泄,月经不调,带下病,痛经', '①现代常用于治疗男子性功能障碍、尿潴留、肾炎、膀胱炎、前列腺炎、功能性子宫出血、阴道炎、遗尿、低血压、神经衰弱、肠炎等。配中极、百会、三阴交主治遗尿；配肾俞、气海主治肾虚尿频；配肾俞、足三里、三阴交、然谷主治男子不育症；配肾俞、太溪主治久痢。\r\n②《名堂》云：若怀胎必不针，若针而胎落，胎多不出。岐伯云：但是积冷虚乏皆宜灸。', '尿潴留（癃闭）,肾炎,膀胱炎,前列腺炎,功能性子宫出血（崩漏）,低血压', '隔物灸仪艾灸时间：30-90分钟；温度：38-52℃；\r\n艾条悬灸时间：10-20分钟；\r\n艾炷灸时间：5-9壮。', '用手食指中指的指腹揉按此穴。', '', '', '向下斜刺1.5-2寸；局部酸胀，可放射至外生殖器.《入门》“关元至诸虚损及老人泄泻，遗精白浊，令人生子”。', '', '');
INSERT INTO `xwDetail` VALUES ('67', '关元俞', '7', 'Guānyuánshū', 'gys', 'BL26', '', '', '在腰部，当第5腰椎棘突下，旁开1.5寸。', '第1步：正坐或俯卧位；<br>第2步：两侧骨盆最高点（髂嵴最高点)连线与后正中线的交点处，为第4腰椎棘突；<br>第3步：从第4腰椎棘突垂直往下推1个椎体，即是第5腰椎棘突；<br>第4步：在第5腰椎棘突下有一凹陷，此凹陷旁开2横指(食指、中指并拢，以中指近端指间关节横纹水平的二指宽度为1.5寸），即为本穴。', '①腰骶痛；②腹胀，泄泻；③小便频数或不利，遗尿。', '隔物灸仪艾灸时间：30-50分钟；温度：38-50℃；<br>艾条悬灸时间：10-15分钟；<br>艾炷灸时间：5-7壮。', '现代常用于治疗肠炎、阳痿、膀胱炎、慢性盆腔炎、腰肌劳损等。配膀胱俞主治腰痛。', '关元俞小肠俞_opt', '67', '①腰骶痛；②腹胀，泄泻；③小便频数或不利，遗尿。', '腹胀,遗尿', '现代常用于治疗肠炎、阳痿、膀胱炎、慢性盆腔炎、腰肌劳损等。配膀胱俞主治腰痛。', '阳痿,膀胱炎,慢性盆腔炎,腰肌劳损,腰痛', '隔物灸仪艾灸时间：30-50分钟；温度：38-50℃；\r\n艾条悬灸时间：10-15分钟；\r\n艾炷灸时间：5-7壮。', '自我按摩，用拇指的指腹端揉按此穴。', '', '', '直刺0．5～1.2寸。《千金》“治消渴小便数，灸腰目在肾俞下三寸，亦夹脊骨两旁各一寸半”。', '', '');
INSERT INTO `xwDetail` VALUES ('68', '关冲', '10', 'Guānchōng', 'gc', 'SJ1', '井穴', '', '在手无名指末节尺侧，距指甲角0.1寸。', '仰掌虚握拳。沿手无名指指甲底部与环指小指（尺）侧缘引线（即掌背交界线，或称赤白肉际处）的交点处，即为本穴。', '①热病、昏厥；②头痛，目赤，耳聋，喉痹等头面五官疾患。', '艾条悬灸：5-10分钟；<br>艾炷灸时间：3-5壮；麦粒灸：3-5壮。', '现代常用于治疗热病、中暑、耳聋、头痛等。配少商、少泽主治咽喉肿痛；配水沟、劳宫主治中暑；配风池、商阳主治热病无汗。', '关冲_opt', '68', '①热病、昏厥；②头痛，目赤，耳聋，喉痹等头面五官疾患。', '头痛,耳鸣、耳聋', '现代常用于治疗热病、中暑、耳聋、头痛等。配少商、少泽主治咽喉肿痛；配水沟、劳宫主治中暑；配风池、商阳主治热病无汗。', '无汗', '艾条悬灸：5-10分钟；\r\n艾炷灸时间：3-5壮；麦粒灸：3-5壮。', '自我按摩，用手拇指掐点此穴。', '', '', '浅刺0.1寸；或点刺出血。为急救要穴之一。《素问病机气宜保命集》“眼大眦痛，刺少阳井穴关冲”。', '', '');
INSERT INTO `xwDetail` VALUES ('69', '关门', '3', 'Guānmén', 'gm', 'ST22', '', '', '在上腹部，当脐中上3寸，距前正中线2寸。', '仰卧位。从肚脐沿前正中线向上量4横指，再水平旁开2横指（拇指），按压有酸胀感处，即为本穴。', '腹胀、腹痛、肠鸣、腹泻等胃肠疾患。', '隔物灸仪艾灸时间：30-50分钟；温度：38-50℃；<br>艾条悬灸时间：5-10分钟；<br>艾炷灸时间：3-5壮。', '现代常用于治疗急慢性胃炎、急慢性肠炎等。配中脘、足三里、上巨虚主治腹胀、腹痛、消化不良。', '关门太乙_opt', '69', '腹胀、腹痛、肠鸣、腹泻等胃肠疾患。', '腹胀,肠鸣,腹泻', '现代常用于治疗急慢性胃炎、急慢性肠炎等。配中脘、足三里、上巨虚主治腹胀、腹痛、消化不良。', '慢性胃炎,急性胃肠炎', '隔物灸仪艾灸时间：30-50分钟；温度：38-50℃；\r\n艾条悬灸时间：5-10分钟；\r\n艾炷灸时间：3-5壮。', '自我按摩，用食中指的指腹端轻轻揉按此穴。', '', '', '直刺0．8～1．2寸。《甲乙》卷十一“腹胀善满，积气，关门主之”。', '', '');
INSERT INTO `xwDetail` VALUES ('70', '养老', '6', 'Yǎnglǎo', 'yl', 'SI6', '郄穴', '', '在前臂背面尺侧，当尺骨小头近端桡侧凹陷中。', '第1步：屈肘，掌心向胸；<br>第2步：在手腕部小指侧可摸到一凸起的高骨；<br>第3步：沿高骨的最高点往大拇指侧（桡侧）推，可触及一骨缝，即为本穴。', '①目视不明；②肩、背、肘、臂酸痛，腰痛。', '隔物灸仪艾灸时间：20-30分钟；温度：38-45℃；<br>艾条悬灸时间：10-20分钟；<br>艾炷灸时间：3-5壮。', '现代常用于治疗视力减退、眼球充血、半身不遂、急性腰扭伤、落枕等。配肩髃主治肩、背、肘疼痛；配睛明、光明主治视力减退。', '068养老_opt', '70', '①目视不明；②肩、背、肘、臂酸痛，腰痛。', '腰痛', '现代常用于治疗视力减退、眼球充血、半身不遂、急性腰扭伤、落枕等。配肩髃主治肩、背、肘疼痛；配睛明、光明主治视力减退。', '落枕', '隔物灸仪艾灸时间：20-30分钟；温度：38-45℃；\r\n艾条悬灸时间：10-20分钟；\r\n艾炷灸时间：3-5壮。', '自我按摩，用拇指的指腹端轻轻揉按此穴。', '', '', '直刺或斜刺0．5～0．8寸。《甲乙》“肩似折，臑似拔，手不能上下，养老主之”。', '', '');
INSERT INTO `xwDetail` VALUES ('71', '内关', '9', 'Nèiguān', 'ng', 'PC6', '络穴；八脉交会穴（通于阴维脉）', '', '在前臂掌侧，当曲泽与大陵的连线上，腕横纹上2寸，掌长肌腱与桡侧腕屈肌腱之间。', '第1步：伸臂仰掌，微曲腕握拳；<br>第2步：在手臂内侧可触摸到两条明显条索状筋（掌长肌腱与桡侧腕屈肌腱）；<br>第3步：从近掌侧腕横纹向上量2横指（大拇指指间关节部位的横径为1寸），两筋之间的凹陷处，即为本穴。', '①心痛，心悸，胸痛、胸闷等心胸病证；②胃痛，呕吐，呃逆等胃疾；③失眠，癫痫等神志病证；④上肢痹痛、偏瘫、手指麻木等局部病证。', '隔物灸仪艾灸时间：30-50分钟；温度：38-48℃；<br>艾条悬灸时间：10-20分钟；<br>艾炷灸时间：5-7壮。', '现代常用于治疗心绞痛、心肌炎、心律不齐、胃炎、癔病。配大陵、神门主治失眠；配郄门主治心痛；配足三里、中脘主治胃痛、吐泻。', '121内关大陵_opt', '71', '①心痛，心悸，胸痛、胸闷等心胸病证；②胃痛，呕吐，呃逆等胃疾；③失眠，癫痫等神志病证；④上肢痹痛、偏瘫、手指麻木等局部病证。', '心律失常（心悸）,胸痛,胸闷,呕吐,呃逆,癫痫', '现代常用于治疗心绞痛、心肌炎、心律不齐、胃炎、癔病。配大陵、神门主治失眠；配郄门主治心痛；配足三里、中脘主治胃痛、吐泻。', '心绞痛,失眠', '隔物灸仪艾灸时间：30-50分钟；温度：38-48℃；\r\n艾条悬灸时间：10-20分钟；\r\n艾炷灸时间：5-7壮。', '自我按摩，用手拇指或食指的指腹端轻轻揉按此穴。', '', '', '直刺0.5-1.5寸，可透外关；斜刺1-2寸，针尖向上肘弯部，酸麻胀感可扩散至肘、腋、胸等处，治疗手指麻木时，针尖略偏向桡侧，刺0.3-0.5寸，麻胀感向指端放散。', '', '');
INSERT INTO `xwDetail` VALUES ('72', '内庭', '3', 'Nèitíng', 'nt', 'ST44', '荥穴', '', '足背第2、3趾间，趾蹼缘后方赤白肉际处。', '第1步：坐位或仰卧位；<br>第2步：在足背第2、3趾的趾蹼缘略后一些（约半横指）的地方，按压有酸胀感处，即为本穴。', '①齿痛，咽喉肿痛，鼻衄等五官热性病证；②热病；③吐酸、腹泻、痢疾、便秘等肠胃病证；④足背肿痛，跖趾关节痛；⑤三叉神经痛。', '隔物灸仪艾灸时间：20-30分钟；温度：38-45℃；<br>艾条悬灸时间：5-10分钟；<br>艾炷灸时间：3-5壮。', '现代常用于治疗急慢性胃炎、急慢性肠炎、齿龈炎、扁桃体炎、跖趾关节痛等。配合谷主治牙龈肿痛；配太冲、曲池、大椎等主治热病。', '陷谷内庭厉兑_opt', '72', '①齿痛，咽喉肿痛，鼻衄等五官热性病证；②热病；③吐酸、腹泻、痢疾、便秘等肠胃病证；④足背肿痛，跖趾关节痛；⑤三叉神经痛。', '牙痛,腹泻,痢疾,便秘,三叉神经痛', '现代常用于治疗急慢性胃炎、急慢性肠炎、齿龈炎、扁桃体炎、跖趾关节痛等。配合谷主治牙龈肿痛；配太冲、曲池、大椎等主治热病。', '慢性胃炎,急性胃肠炎,扁桃体炎', '隔物灸仪艾灸时间：20-30分钟；温度：38-45℃；\r\n艾条悬灸时间：5-10分钟；\r\n艾炷灸时间：3-5壮。', '自我按摩，用拇指的指腹端轻轻揉按此穴。', '', '', '直刺或斜刺0．5～0．8寸。', '', '');
INSERT INTO `xwDetail` VALUES ('73', '内踝尖', '15', 'Nèihuáijiān', 'nhj', 'EX-LE8', '', '', '在足内侧，内踝的凸起处。', '第1步：仰卧位或正坐垂足；<br>第2步：内踝最高点，即为本穴。', '①乳蛾，牙痛；②小儿不语；③霍乱转筋。', '隔物灸仪艾灸时间：30-50 分钟；温度：38-45 ℃；<br>艾条悬灸时间：5-10分钟。', '', '内踝尖_opt', '73', '①乳蛾，牙痛；②小儿不语；③霍乱转筋。', '小儿乳蛾,牙痛', '', '', '隔物灸仪艾灸时间：30-50 分钟；温度：38-45 ℃；\r\n艾条悬灸时间：5-10分钟。', '', '', '', '', '', '');
INSERT INTO `xwDetail` VALUES ('74', '内迎香', '15', 'Nèiyíngxiāng', 'nyx', 'EX-HN9', '', '', '仰靠坐位。在鼻孔内，当鼻翼软骨与鼻甲交界的黏膜处。', '正坐或仰卧位。与上迎香相对处鼻黏膜处。', '①目赤肿痛，鼻塞，鼻渊，喉痹等五官病证；②热病，中暑。', '禁灸。', '现代常用于治疗急性结膜炎、鼻炎、鼻窦炎、中暑等。配印堂、合谷、大椎、风池主治鼻炎。', '夹承浆内迎香_opt', '74', '①目赤肿痛，鼻塞，鼻渊，喉痹等五官病证；②热病，中暑。', '鼻渊', '现代常用于治疗急性结膜炎、鼻炎、鼻窦炎、中暑等。配印堂、合谷、大椎、风池主治鼻炎。', '急性结膜炎,过敏性鼻炎', '禁灸。', '', '', '', '用三棱针点刺出血。', '', '');
INSERT INTO `xwDetail` VALUES ('75', '冲门', '4', 'Chōngmén', 'cm', 'SP12', '足太阴、厥阴经交会穴', '', '在腹股沟外侧，距耻骨联合上缘中点3.5寸，当髂外动脉搏动处的外侧。', '第1步：仰卧位；<br>第2步：先在腹股沟外侧触摸到搏动的动脉（髂外动脉）；<br>第3步：在搏动处外侧按压有酸胀感，即为本穴。', '①腹痛、疝气；②崩漏、带下、胎气上冲等妇科病证；③乳腺炎，乳少。', '隔物灸仪艾灸时间：20-30分钟；温度：38-45℃；<br>艾条悬灸时间：10-20分钟；<br>间接灸时间：3-5壮。', '现代常用于治疗疝气、尿潴留、睾丸炎、子宫内膜炎等。配气冲治疗带下；配天枢、太冲等主治疝气。', '043冲门府舍腹结_opt', '75', '①腹痛、疝气；②崩漏、带下、胎气上冲等妇科病证；③乳腺炎，乳少。', '腹痛,功能性子宫出血（崩漏）,带下病,乳腺炎', '现代常用于治疗疝气、尿潴留、睾丸炎、子宫内膜炎等。配气冲治疗带下；配天枢、太冲等主治疝气。', '尿潴留（癃闭）,睾丸炎', '隔物灸仪艾灸时间：20-30分钟；温度：38-45℃；\r\n艾条悬灸时间：10-20分钟；\r\n间接灸时间：3-5壮。', '自我按摩，用拇指的指腹端轻轻揉按此穴。', '', '', '避开动脉，直刺0．5～l寸。', '', '');
INSERT INTO `xwDetail` VALUES ('76', '冲阳', '3', 'Chōngyáng', 'cy', 'ST42', '原穴', '', '在足背最高处，当拇长伸肌腱与趾长伸肌腱之间，足背动脉搏动处。', '第1步：正坐垂足或仰卧；<br>第2步：在足背最高点，两筋之间可触及一凹陷，按之有搏动感，即为本穴。', '①足痿无力，风湿性关节炎，足扭伤；②口眼歪斜；③癫狂痫；④胃痛。', '隔物灸仪艾灸时间：30-40分钟；温度：38-45℃；<br>艾条悬灸时间：5-10分钟；<br>艾炷灸时间：3-5壮。', '现代常用于精神分裂症、足下垂、脉管炎等。配足三里、仆参、飞扬主治足痿；配丰隆主治癫狂。', '解溪冲阳_opt', '76', '①足痿无力，风湿性关节炎，足扭伤；②口眼歪斜；③癫狂痫；④胃痛。', '风湿性关节炎', '现代常用于精神分裂症、足下垂、脉管炎等。配足三里、仆参、飞扬主治足痿；配丰隆主治癫狂。', '血栓闭塞性脉管炎', '隔物灸仪艾灸时间：30-40分钟；温度：38-45℃；\r\n艾条悬灸时间：5-10分钟；\r\n艾炷灸时间：3-5壮。', '自我按摩，用拇指的指腹端轻轻揉按此穴。', '', '', '避开动脉，直刺0．3～0．5寸。', '', '');
INSERT INTO `xwDetail` VALUES ('77', '列缺', '1', 'Lièquē', 'lq', 'LU7', '络脉，八脉交会穴，通任脉', '', '在前臂桡侧缘，桡骨茎突上方，腕横纹上1.5寸，当肱桡肌与拇长展肌腱之间。', '第1步：两虎口相交；<br>第2步：一手食指压在另一手的桡骨茎突（掌心向前，手腕外侧突起的骨头）上；<br>第3步：在食指尖端到达的凹陷处，触摸时可感有一裂隙，即为本穴。', '①咳嗽、气喘、咽喉肿痛等肺系病证；②头痛、齿痛、项强、口眼歪斜等头项部疾患；③颈椎病，腕关节周围软组织疾病。', '艾条悬灸：5-10分钟；<br>艾炷灸时间3-5壮。', '现代常用于治疗感冒、支气管炎、神经性头痛、落枕、腕关节及周围软组织疾患等。配风池、风门等主治感冒、咳嗽、头痛等；配合谷、外关主治项强等；配照海主治咽喉疼痛。《针灸资生经》：”若患偏风灸至百，若患腕劳灸七七。', '列缺_opt', '77', '①咳嗽、气喘、咽喉肿痛等肺系病证；②头痛、齿痛、项强、口眼歪斜等头项部疾患；③颈椎病，腕关节周围软组织疾病。', '咳嗽,气喘,头痛,牙痛,项强,颈椎病', '现代常用于治疗感冒、支气管炎、神经性头痛、落枕、腕关节及周围软组织疾患等。配风池、风门等主治感冒、咳嗽、头痛等；配合谷、外关主治项强等；配照海主治咽喉疼痛。《针灸资生经》：”若患偏风灸至百，若患腕劳灸七七。', '感冒,支气管炎,落枕', '第1步：两虎口相交；\r\n第2步：一手食指压在另一手的桡骨茎突（掌心向前，手腕外侧突起的骨头）上；\r\n第3步：在食指尖端到达的凹陷处，触摸时可感有一裂隙，即为本穴。', '自我按摩，食指指腹点按揉运穴位的酸胀处3分钟。', '', '', '斜刺，针尖向肘微斜刺入0．2～0.5寸，不可深刺，局部酸困，或向肘肩部放散。除能治疗本经咳喘等症外，长于治疗偏正头痛、颈项强痛、口眼歪斜等症。又列缺为八脉交会穴通任脉，可以用搓针法治疗尿储留、埋针治疗小儿遗尿及痛经等任脉疾患。', '', '');
INSERT INTO `xwDetail` VALUES ('78', '前谷', '6', 'Qiángǔ', 'qg', 'SI2', '荥穴', '', '在手尺侧，微握拳，当小指本节（第5掌指关节）前的掌指横纹头赤白肉际。', '第1步：仰掌握拳；<br>第2步：在手掌内侧，小指掌指关节前，有一皮肤皱襞，其尖端即为本穴。', '①热病；②乳痈、乳汁少等乳疾；③头痛、目痛、耳鸣、咽喉肿痛等头面五官病证。', '艾条悬灸：5-10分钟；<br>艾炷灸时间：3-5壮；麦粒灸：3-5壮。', '现代常用于治疗扁桃体炎、乳腺炎、腮腺炎、前臂神经痛、高热、精神分裂症等。配风池、大椎主治癫狂；配合谷主治热病汗不出。', '前谷后溪_opt', '78', '①热病；②乳痈、乳汁少等乳疾；③头痛、目痛、耳鸣、咽喉肿痛等头面五官病证。', '产后缺乳,头痛,耳鸣、耳聋', '现代常用于治疗扁桃体炎、乳腺炎、腮腺炎、前臂神经痛、高热、精神分裂症等。配风池、大椎主治癫狂；配合谷主治热病汗不出。', '扁桃体炎,乳腺炎,流行性腮腺炎', '艾条悬灸：5-10分钟；\r\n艾炷灸时间：3-5壮；麦粒灸：3-5壮。', '自我按摩，用拇指的指腹端轻轻揉按此穴。', '', '', '直刺0．3～0．5寸。', '', '');
